INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (675, 'Política de Ciência, Tecnologia e Inovação de Defesa', '2021-01-01', 'Não se aplica', 'Não se aplica', '3063/2021', '2021-07-22', null, 'Art. 2º A Política de Ciência, Tecnologia e Inovação de Defesa tem como objetivo geral estimular o desenvolvimento científico e tecnológico e a inovação de interesse de Defesa. Art. 3º São objetivos específicos da Política de Ciência, Tecnologia e Inovação de Defesa: I - ampliar o conteúdo tecnológico dos produtos e serviços de interesse de Defesa; II - elevar o nível de capacitação de recursos humanos, vinculados ou dedicados à ciência, tecnologia e inovação de interesse de Defesa; III - aprimorar a infraestrutura de ciência, tecnologia e inovação de apoio a programas e projetos prioritários de interesse de Defesa; IV - proporcionar a criação de ambiente favorável à inovação, à cooperação, à competitividade industrial e à capacitação nacional, voltados aos interesses de Defesa; V - aperfeiçoar os mecanismos de financiamento das atividades de ciência, tecnologia e inovação de interesse de Defesa; VI - promover a cultura de Ciência, Tecnologia e Inovação de interesse da Defesa, junto a diversos segmentos da sociedade; VII - estimular o aproveitamento da imagem de excelência institucional; VIII - integrar as iniciativas de ciência, tecnologia e inovação de interesse de Defesa, conduzidas nas Instituições Científicas, Tecnológicas e de Inovação (ICT), nas empresas e na academia; IX - estabelecer mecanismos voltados a políticas para a valorização de recursos humanos baseada em resultados; e X - implantar sistemática que integre o planejamento estratégico, o ciclo de vida de produtos e serviços de interesse de Defesa e a avaliação de resultados', null, 'NA', 'Portaria', 8, 3, 9, null, null, 'Sem especificação');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (676, 'Política de Educação Física e Desportos da Marinha do Brasil', '2021-01-01', 'Não se aplica', 'Não se aplica', ' 08/2021', '2021-03-15', null, 'OBJETIVOS DA EDUCAÇÃO FÍSICA E DESPORTOS DA MB Os objetivos a serem alcançados dividem-se em dois grandes segmentos. O primeiro diz respeito a "propiciar ao pessoal da MB a higidez física adequada às exigências da vida naval". Este segmento está diretamente associado ao TFM. São objetivos elaborados respeitando os princípios científicos e afins, considerando tanto as especificidades das OM, ou seja, as "OM Tipo", quanto às demandas referentes ao emprego de seus efetivos, de modo a garantir o atingimento da higidez física adequada às tarefas de cada Unidade. Assim, é mister que os Comandos tenham um completo entendimento da validade e quase imprescindível necessidade de que os militares, independente de suas áreas de atuação, tenham a melhor condição física possível. O segundo segmento refere-se aos atletas que representarão a MB e o Brasil. Para participar de competições nacionais e internacionais, em condições de obter as primeiras colocações, esses militares atletas terão que ser "profissionais do esporte". Serão militares que terão a missão exclusiva de defender a marca "Marinha do Brasil" e as cores brasileiras nas diversas competições esportivas, primordialmente nas modalidades militares. Para não impactar demasiadamente as Unidades com a cessão de seus militares de carreira, manter-se-á a incorporação de atletas de alto rendimento como Praças temporários (RM2). Subsidiariamente, buscar-se-á a ampliação da mentalidade desportiva no âmbito da Força, permitindo aos Comandos uma maior compreensão da real importância de atletas de alto rendimento para a divulgação positiva da MB no segmento civil da sociedade brasileira. Não obstante, é de suma importância buscar o aumento na quantidade de militares qualificados nos cursos oferecidos e (ou) intermediados pelo Centro de Educação Física Almirante Adalberto Nunes (CEFAN), como: Cursos Expedito de TFM para Oficiais e Praças; Estágio de Qualificação em Educação Física para Fuzileiros Navais; e os Cursos de Instrutor (Oficiais) e Monitor (Praças) da Escola de Educação Física do Exército.', null, 'NA', 'Portaria', 8, 2, 26, null, null, 'Sem especificação');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (677, 'Política de Gestão da Informação do Ministério da Defesa', '2021-01-01', 'Não se aplica', 'Não se aplica', '3070/2021', '2021-07-22', null, 'Art. 2º A Política de Gestão da Informação do Ministério da Defesa tem por objetivo ampliar os instrumentos de transparência pública, indispensáveis ao processo de boas práticas de gestão governamental e de proteção de informações necessárias à segurança da sociedade e do Estado, em estreito alinhamento com os pressupostos de direitos fundamentais.', null, 'NA', 'Portaria', 8, 6, 8, null, null, 'Sem especificação');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (678, 'Política de Propriedade Intelectual do Ministério da Defesa', '2021-01-01', 'Não se aplica', 'Não se aplica', '3439/2021', '2021-08-18', null, 'Art. 2º A Política de Propriedade Intelectual do Ministério da Defesa tem como objetivo geral orientar as ações relacionadas: I - à proteção do conhecimento e das criações desenvolvidas pelas Instituições Científicas, Tecnológicas e de Inovação (ICT) qualificadas ou reconhecidas no âmbito do Ministério da Defesa ou dos Comandos das Forças Armadas, incluindo proteções requeridas e concedidas; e II - aos contratos firmados de licenciamento de criações protegidas e de transferência de tecnologia. Parágrafo único. Para os fins do disposto nesta Portaria, as ICT de que trata o inciso I serão denominadas ICT do Ministério da Defesa. Art. 3º São objetivos específicos da Política de Propriedade Intelectual do Ministério da Defesa: I - criar um ambiente capaz de estimular a preservação da propriedade intelectual; II - capacitar e valorizar os recursos humanos envolvidos nos processos de geração de novos conhecimentos e de proteção da propriedade intelectual; III - fomentar o licenciamento de criação protegida e a transferência de tecnologias geradas no âmbito das ICT do Ministério da Defesa; e IV - estimular o uso da informação de Propriedade Intelectual como ferramenta estratégica para nortear pesquisas, desenvolvimentos, inovações, negócios e geração de ativos de propriedade intelectual.', null, 'NA', 'Portaria', 8, 6, 8, null, null, 'Sem especificação');
