INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (418, 'Programa Nacional de Imunizações (PNI)', '1973-01-01', 'Não se aplica', 'Não se aplica', '311/1973', '1973-11-20', null, 'Lei 6259/1975 Art 1º Consoante as atribuições que lhe foram conferidas dentro do Sistema Nacional de Saúde, na forma do artigo 1º da Lei nº 6.229, inciso I e seus itens a e d , de 17 de julho de 1975, o Ministério da Saúde, coordenará as ações relacionadas com o controle das doenças transmissíveis, orientando sua execução inclusive quanto à vigilância epidemiológica, à aplicação da notificação compulsória, ao programa de imunizações e ao atendimento de agravos coletivos à saúde, bem como os decorrentes de calamidade pública. Parágrafo único. Para o controle de epidemias e na ocorrência de casos de agravo à saúde decorrentes de calamidades públicas, o Ministério da Saúde, na execução das ações de que trata este artigo, coordenará a utilização de todos os recursos médicos e hospitalares necessários, públicos e privados, existentes nas áreas afetadas, podendo delegar essa competência às Secretarias de Saúde dos Estados, do Distrito Federal e dos Territórios. ', 'O programa foi criado em 1973 pelo Ministério da Saúde. Foi referendado posteriormente em 1975 pela Lei 6259. Esta lei foi regulamentada pelo Decreto 78.231, de 1976.', 'Ação 20YE', 'Lei ordinária', 5, 1, 1, null, null, 'População em geral');
