INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (516, 'Política Nacional de Atenção Básica', '2011-01-01', 'Não se aplica', 'Não se aplica', '2488/2011', '2011-10-21', '2017-09-21', 'A Atenção Básica caracteriza-se por um conjunto de ações de saúde, no âmbito individual e coletivo, que abrange a promoção e a proteção da saúde, a prevenção de agravos, o diagnóstico, o tratamento, a reabilitação, redução de danos e a manutenção da saúde com o objetivo de desenvolver uma atenção integral que impacte na situação de saúde e autonomia das pessoas e nos determinantes econdicionantes de saúde das coletividades. É desenvolvida por meio do exercício de práticas de cuidado e gestão, democráticas e participativas, sob forma de trabalho em equipe, dirigidas a populações de territórios definidos, pelas quais assume a responsabilidade sanitária, considerando a dinamicidade existente no território em que vivem essas populações. Utiliza tecnologias de cuidado complexas e variadas que devem auxiliar no manejo das demandas e necessidades de saúde de maior freqüência e relevância em seu território, observando critérios de risco, vulnerabilidade, resiliência e o imperativo ético de que toda demanda, necessidade de saúde ou sofrimento devem ser acolhidos.', 'Modificada pela PNAB 2017', 'Política com ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'Pessoas afetadas por doenças');
