INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (519, 'Política Nacional de Atenção em Oftalmologia', '2008-01-01', 'Não se aplica', 'Não se aplica', '957/2008', '2008-05-15', null, 'Art. 2º - Estabelecer que a Política Nacional de Atenção em Oftalmologia seja organizada de forma articulada entre o Ministério da Saúde, as Secretarias de Estado da Saúde e do Distrito Federal e as Secretarias Municipais de Saúde, por intermédio de redes estaduais e regionais, permitindo: I - desenvolver estratégias de promoção da qualidade de vida, educação, proteção e recuperação da saúde e prevenção de danos, protegendo e desenvolvendo a autonomia e a equidade de indivíduos e coletividades; II - organizar uma linha de cuidados integrais (promoção, prevenção, tratamento e recuperação) que perpasse todos os níveis de atenção, promovendo, dessa forma, a inversão do modelo de atenção; III - identificar os determinantes e condicionantes das principais patologias que levam à doença oftalmológica e desenvolver ações transetoriais de responsabilidade pública, sem excluir as responsabilidades de toda a sociedade; IV - definir critérios técnicos mínimos para o funcionamento e avaliação das Unidades de Atenção Especializada, públicas ou privadas que prestam atenção em oftalmologia, bem como os mecanismos de sua monitorização com vistas à diminuição dos riscos aos quais fica exposto o paciente com doença oftalmológica; V - ampliar a cobertura no atendimento aos pacientes com doenças oftalmológicas no Brasil, garantindo a universalidade, a eqüidade, a integralidade, o controle social e o acesso às Unidades de Atenção Especializada em Oftalmologia; VI - contribuir para o desenvolvimento de processos e métodos de coleta, análise e organização dos resultados das ações decorrentes da Política Nacional de Atenção em Oftalmologia, permitindo que a partir de seu desempenho seja possível um aprimoramento da gestão, disseminação das informações e uma visão dinâmica do estado de saúde das pessoas com doenças oftalmológicas; VII - promover intercâmbio com outros subsistemas de informações setoriais, implementando e aperfeiçoando permanentemente a produção de dados e garantindo a democratização das informações;e VIII - qualificar a assistência e promover a educação permanente dos profissionais de saúde envolvidos com a implantação e implementação da Política Nacional de Atenção em Oftalmologia, em acordo com os princípios da integralidade e da humanização.', '#N/A', 'Política sem ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'Pessoas afetadas por doenças');
