INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (524, 'Política Nacional de Atenção Integral à Saúde do Homem', '2009-01-01', 'Não se aplica', 'Não se aplica', '1944/2009', '2009-08-27', null, 'Art. 4º São objetivos da Política Nacional de Atenção Integral à Saúde do Homem: I - promover a mudança de paradigmas no que concerne à percepção da população masculina em relação ao cuidado com a sua saúde e a saúde de sua família; II - captar precocemente a população masculina nas atividades de prevenção primária relativa às doenças cardiovasculares e cânceres, entre outros agravos recorrentes; III - organizar, implantar, qualificar e humanizar, em todo o território brasileiro, a atenção integral à saúde do homem; IV - fortalecer a assistência básica no cuidado com o homem, facilitando e garantindo o acesso e a qualidade da atenção necessária ao enfrentamento dos fatores de risco das doenças e dos agravos à saúde; V - capacitar e qualificar os profissionais da rede básica para o correto atendimento à saúde do homem; VI - implantar e implementar a atenção à saúde sexual e reprodutiva dos homens, incluindo as ações de planejamento e assistência às disfunções sexuais e reprodutivas, com enfoque na infertilidade; VII - ampliar e qualificar a atenção ao planejamento reprodutivo masculino; VIII - estimular a participação e a inclusão do homem nas ações de planejamento de sua vida sexual e reprodutiva, enfocando as ações educativas, inclusive no que toca à paternidade; IX - garantir a oferta da contracepção cirúrgica voluntária masculina nos termos da legislação específica; X - promover a prevenção e o controle das doenças sexualmente transmissíveis e da infecção pelo HIV; XI - garantir o acesso aos serviços especializados de atenção secundária e terciária; XII - promover a atenção integral à saúde do homem nas populações indígenas, negras, quilombolas, gays, bissexuais, travestis, transexuais, trabalhadores rurais, homens com deficiência, em situação de risco, e em situação carcerária, entre outros; XIII - estimular a articulação das ações governamentais com as da sociedade civil organizada, a fim de possibilitar o protagonismo social na enunciação das reais condições de saúde da população masculina, inclusive no tocante à ampla divulgação das medidas preventivas; XIV -ampliar o acesso às informações sobre as medidas preventivas contra os agravos e as enfermidades que atingem a população masculina; XV - incluir o enfoque de gênero, orientação sexual, identidade de gênero e condição étnico-racial nas ações socioeducativas; XVI - estimular, na população masculina, o cuidado com sua própria saúde, visando à realização de exames preventivos regulares e à adoção de hábitos saudáveis; e XVII - aperfeiçoar os sistemas de informação de maneira a possibilitar um melhor monitoramento que permita tomadas de decisão.', '#N/A', 'Política sem ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'Outros');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (525, 'Política Nacional de Atenção Integral à Saúde do Povo Cigano/Romani', '2018-01-01', 'Não se aplica', 'Não se aplica', '4384/2018', '2018-12-28', null, 'Art. 3º A Política Nacional de Atenção Integral à Saúde do Povo Cigano/Romani tem como objetivo geral promover a saúde integral do Povo Cigano/Romani, respeitando suas práticas, saberes e medicinas tradicionais, priorizando a redução e o combate à ciganofobia ou romafobia.
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (526, 'Política Nacional de Atenção Integral às Pessoas com Doença Falciforme e outras Hemoglobinopatias', '2005-01-01', 'Não se aplica', 'Não se aplica', '1391/2005', '2005-08-16', null, 'Parágrafo único. As diretrizes de que trata este artigo  apóiam-se  na promoção, na prevenção, no diagnóstico precoce, no tratamento e na reabilitação de agravos à saúde, articulando as áreas técnicas cujas ações têm interface com o atendimento hematológico e hemoterápico.', '#N/A', 'Política com ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'Pessoas afetadas por doenças');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (527, 'Política Nacional de Atenção Integral às Pessoas com Doenças Raras', '2014-01-01', 'Não se aplica', 'Não se aplica', '199/2014', '2014-01-30', null, 'Art. 4º A Política Nacional de Atenção Integral às Pessoas com Doenças Raras tem como objetivo reduzir a mortalidade, contribuir para a redução da morbimortalidade e das manifestações secundárias e a melhoria da qualidade de vida das pessoas, por meio de ações de promoção, prevenção, detecção precoce, tratamento oportuno redução de incapacidade e cuidados paliativos.
