INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (529, 'Política Nacional de Atenção Integral em Reprodução Humana Assistida', '2005-01-01', 'Não se aplica', 'Não se aplica', '426/2005', '2005-03-22', null, 'Art. 2°  Determinar que a Política Nacional de Atenção Integral em Reprodução Humana Assistida seja implantada de forma articulada entre o Ministério da Saúde, as Secretarias de Estado de Saúde e as Secretarias Municipais de Saúde, permitindo: I - organizar uma linha de cuidados integrais (promoção, prevenção, tratamento e reabilitação) que perpasse todos os níveis de atenção, promovendo, dessa forma, a atenção por intermédio de equipe multiprofissional, com atuação interdisciplinar; II - identificar os determinantes e condicionantes dos principais problemas de infertilidade em casais em sua vida fértil, e desenvolver ações transetoriais de responsabilidade pública, sem excluir as responsabilidades de toda a sociedade; III - definir critérios técnicos mínimos para o funcionamento, o monitoramento e a avaliação dos serviços que realizam os procedimentos e técnicas de reprodução humana assistida, necessários à viabilização da concepção, tanto para casais com infertilidade, como para aqueles que se beneficiem desses recursos para o controle da transmissão vertical e/ou horizontal de doenças; IV - fomentar, coordenar e executar projetos estratégicos que visem ao estudo do custo-efetividade, eficácia e qualidade, bem como a incorporação tecnológica na área da reprodução humana assistida no Brasil; V - promover intercâmbio com outros subsistemas de informações setoriais, implementando e aperfeiçoando permanentemente a produção de dados e garantindo a democratização das informações; e VI - qualificar a assistência e promover a educação permanente dos profissionais de saúde envolvidos com a implantação e a implementação da Política de Atenção Integral em Reprodução Humana Assistida, em conformidade com os princípios da integralidade e da Política Nacional de Humanização - PNH.', '#N/A', 'Política sem ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'Pessoas afetadas por doenças');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (530, 'Política Nacional de Atenção Obstétrica e Neonatal', '2005-01-01', 'Não se aplica', 'Não se aplica', '1067/2005', '2005-07-04', null, 'Parágrafo único. A Política Nacional de Atenção Obstétrica e Neonatal será executada conjuntamente pelo Ministério da Saúde e as Secretarias de Saúde dos Estados, dos Municípios e do Distrito Federal e tem por objetivo o desenvolvimento de ações de promoção, prevenção e assistência à saúde de gestantes e recém-nascidos, promovendo a ampliação do acesso a essas ações, o incremento da qualidade da assistência obstétrica e neonatal, bem como sua organização e regulação no âmbito do Sistema Único de Saúde.', '#N/A', 'Política sem ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'Gestantes, puérperas e recém-nascidos');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (531, 'Política Nacional de Atenção Oncológica', '2005-01-01', 'Não se aplica', 'Não se aplica', '2439/2005', '2005-12-08', '2013-05-16', 'Art. 2°  Estabelecer que a Política Nacional de Atenção Oncológica deve ser organizada de forma articulada com o Ministério da Saúde e com as Secretarias de Saúde dos estados e dos municípios, permitindo: I - desenvolver estratégias coerentes com a política nacional de promoção da saúde voltadas para a identificação dos determinantes e condicionantes das principais neoplasias malignas e orientadas para o desenvolvimento de ações intersetoriais de responsabilidade pública e da sociedade civil que promovam a qualidade de vida e saúde, capazes de prevenir fatores de risco, reduzir danos e proteger a vida de forma a garantir a eqüidade e a autonomia de indivíduos e coletividades; II - organizar uma linha de cuidados que perpasse todos os níveis de atenção (atenção básica e atenção especializada de média e alta complexidades) e de atendimento (promoção, prevenção, diagnóstico, tratamento, reabilitação e cuidados paliativos); III - constituir Redes Estaduais ou Regionais de Atenção Oncológica, formalizadas nos Planos Estaduais de Saúde, organizadas em níveis hierarquizados, com estabelecimento de fluxos de referência e contra-referência, garantindo acesso e atendimento integral; IV - definir critérios técnicos adequados para o funcionamento e avaliação dos serviços públicos e privados que atuam nos diversos níveis da atenção oncológica, bem como os mecanismos de sua monitoração; V - ampliar a cobertura do atendimento aos doentes de câncer, garantindo a universalidade, a eqüidade, a integralidade, o controle social e o acesso à assistência oncológica; VI - fomentar, coordenar e executar projetos estratégicos de incorporação tecnológica, mediante estudos de custo-efetividade, eficácia e qualidade e avaliação tecnológica da atenção oncológica no Brasil; VII - contribuir para o desenvolvimento de processos e métodos de coleta, análise e organização dos resultados das ações decorrentes da Política Nacional de Atenção Oncológica, permitindo o aprimoramento da gestão e a disseminação das informações; VIII - promover intercâmbio com outros subsistemas de informações setoriais, implementando e aperfeiçoando permanentemente a produção de dados e a democratização das informações com a perspectiva de usá-las para alimentar estratégias promocionais da saúde; IX - qualificar a assistência e promover a educação permanente dos profissionais de saúde envolvidos com a implantação e a implementação da Política de Atenção Oncológica, em acordo com os princípios da integralidade e da humanização; X - fomentar a formação e a especialização de recursos humanos para a rede de atenção oncológica; e XI - incentivar a pesquisa na atenção oncológica, de acordo com os objetivos da Política Nacional de Ciência e Tecnologia em Saúde.', '#N/A', 'Política com ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'Pessoas afetadas por doenças');
