INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (552, 'Política Nacional de Saúde Integral das Populações do Campo, da Floresta e das Águas (PNSIPCFA)', '2011-01-01', 'Não se aplica', 'Não se aplica', '2866/2011', '2011-12-02', null, 'Art. 3º A PNSIPCF tem os seguintes objetivos específicos: I - garantir o acesso aos serviços de saúde com resolutividade, qualidade e humanização, incluindo as ações de atenção, as especializadas de média e alta complexidade e as de urgência e de emergência, de acordo com as necessidades e demandas apontadas pelo perfil epidemiológico da população atendida; II - contribuir para a redução das vulnerabilidades em saúde das populações do campo e da floresta, desenvolvendo ações integrais voltadas para a saúde do idoso, da mulher, da pessoa com deficiência, da criança e do adolescente, do homem e do trabalhador, considerando a saúde sexual e reprodutiva, bem como a violência sexual e doméstica; III - reduzir os acidentes e agravos relacionados aos processos de trabalho no campo e na floresta, particularmente o adoecimento decorrente do uso de agrotóxicos e mercúrio, o advindo do risco ergonômico do trabalho no campo e na floresta e da exposição contínua aos raios ultravioleta; IV - contribuir para a melhoria da qualidade de vida das populações do campo e da floresta, incluindo articulações intersetoriais para promover a saúde, envolvendo ações de saneamento e meio ambiente, especialmente para a redução de riscos sobre a saúde humana; V - reconhecer e valorizar os saberes e as práticas tradicionais de saúde das populações do campo e da floresta, respeitando suas especificidades; VI - promover planejamentos participativos capazes de identificar as demandas de saúde das populações do campo e da floresta e definir metas, estratégias e ações específicas para sua atenção; VII - incluir no processo de educação permanente dos trabalhadores de saúde as temáticas e os conteúdos relacionados às necessidades, demandas e especificidades das populações do campo e da floresta, considerando a interculturalidade na atenção aos povos e comunidades tradicionais; VIII - apoiar processos de educação e informação das populações do campo e da floresta sobre o direito à saúde; IX - apoiar a expansão da participação das representações dessas populações nos Conselhos de Saúde estaduais, distrital e municipais e em outros espaços de gestão participativa; X - promover mecanismos de informação e comunicação, de acordo com a diversidade e as especificidades socioculturais; XI - incentivar a pesquisa e a produção de conhecimento sobre os riscos, a qualidade de vida e a saúde das populações do campo e da floresta, respeitando as especificidades de geração, raça/cor, gênero, etnia e orientação sexual; e XII - promover o fortalecimento e a ampliação do sistema público de vigilância em saúde, do monitoramento e da avaliação tecnológica sobre os agravos à saúde decorrentes do uso de agrotóxicos e transgênicos.', '#N/A', 'Política com ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'Trabalhadores Rurais');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (553, 'Política Nacional de Saúde Integral de Lésbicas, Gays, Bissexuais, Travestis e Transexuais', '2011-01-01', 'Não se aplica', 'Não se aplica', '2836/2011', '2011-12-01', null, 'Art. 1º Esta Portaria institui a Política Nacional de Saúde Integral de Lésbicas, Gays, Bissexuais, Travestis e Transexuais (Política Nacional de Saúde Integral LGBT) no âmbito do SUS, com o objetivo geral de promover a saúde integral da população LGBT, eliminando a discriminação e o preconceito institucional e contribuindo para a redução das desigualdades e para consolidação do SUS como sistema universal, integral e equitativo.
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (554, 'Política Nacional de Transportes', '2018-01-01', 'Não se aplica', 'Não se aplica', '235/2018', '2018-03-29', null, 'Art. 4° A Política Nacional de Transportes tem por objetivos: I - prover um sistema acessível, eficiente e confiável para a mobilidade de pessoas e bens; II - garantir a segurança operacional em todos os modos de transportes;
