INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (558, 'Programa Academia da Saúde', '2013-01-01', 'Não se aplica', 'Não se aplica', '2681/2013', '2011-04-07', null, 'Art. 2o O Programa Academia da Saúde tem como objetivo principal contribuir para a promoção da saúde e produção do cuidado e de modos de vida saudáveis da população a partir da implantação de polos com infraestrutura e profissionais qualificados. Art. 5o- São objetivos específicos do Programa Academia da Saúde: I - ampliar o acesso da população às políticas públicas de promoção da saúde; II - fortalecer a promoção da saúde como estratégia de produção de saúde; III - desenvolver a atenção à saúde nas linhas de cuidado, a fim de promover o cuidado integral; IV - promover práticas de educação em saúde; V - promover ações intersetoriais com outros pontos de atenção da Rede de Atenção à Saúde e outros equipamentos sociais do território; VI - potencializar as ações nos âmbitos da atenção básica, da vigilância em saúde e da promoção da saúde; VII - promover a integração multiprofissional na construção e na execução das ações; VIII - promover a convergência de projetos ou programas nos âmbitos da saúde, educação, cultura, assistência social, esporte e lazer; IX - ampliar a autonomia dos indivíduos sobre as escolhas de modos de vida mais saudáveis; X - aumentar o nível de atividade física da população; XI - promover hábitos alimentares saudáveis; XII - promover mobilização comunitária com a constituição de redes sociais de apoio e ambientes de convivência e solidariedade; XIII - potencializar as manifestações culturais locais e o conhecimento popular na construção de alternativas individuais e coletivas que favoreçam a promoção da saúde; e XIV - contribuir para ampliação e valorização da utilização dos espaços públicos de lazer, como proposta de inclusão social, enfrentamento das violências e melhoria das condições de saúde e qualidade de vida da população. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2013/prt2681_07_11_2013.html revoga a PORTARIA Nº 719/2011 https://bvsms.saude.gov.br/bvs/saudelegis/gm/2011/prt0719_07_04_2011.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'Sistema de Saúde');
