INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (604, 'Programa Nacional de Atenção Comunitária Integrada a Usuários de Álcool e Outras Drogas', '2002-01-01', 'Não se aplica', 'Não se aplica', '816/2002', '2002-04-30', null, 'Art. 1º Instituir, no âmbito do Sistema Único de Saúde, o Programa Nacional de Atenção Comunitária Integrada a Usuários de Álcool e Outras Drogas, a ser desenvolvido de forma articulada pelo Ministério da Saúde e pelas Secretarias de Saúde dos estados, Distrito Federal e municípios, tendo por objetivos: I – Articular as ações desenvolvidas pelas três esferas de governo destinadas a promover a atenção aos pacientes com dependência e/ou uso prejudicial de álcool ou outras drogas; II - Organizar e implantar rede estratégica de serviços extra-hospitalares de atenção aos pacientes com esse tipo de transtorno, articulada à rede de atenção psicossocial; III - Aperfeiçoar as intervenções preventivas como forma de reduzir os danos sociais e à saúde representados pelo uso prejudicial de álcool e outras drogas; IV - Realizar ações de atenção/assistência aos pacientes e familiares, de forma integral e abrangente, com atendimento individual, em grupo, atividades comunitárias, orientação profissional, suporte medicamentoso,  psicoterápico, de orientação e outros; V - Organizar/regular as demandas e os fluxos assistenciais; VI - Promover, em articulação com instituições formadoras, a capacitação e supervisão das equipes de atenção básica, serviços e programas de saúde mental locais.', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2002/prt0816_30_04_2002.html', 'Ação 0844 (2004), 8529 (2005 a 2007) e 8585 para custeio (e ações anteriores, da MAC, gestão plena etc)', 'Portaria', 5, 1, 1, null, null, 'Pessoas afetadas por doenças');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (605, 'Programa Nacional de Avaliação de Serviços de Saúde (PNASS)', '2015-01-01', 'Não se aplica', 'Não se aplica', '28/2015', '2005-03-10', null, 'Art. 2º O PNASS tem como objetivo geral avaliar a eficiência, eficácia e efetividade das estruturas, processos e resultados relacionados ao risco, acesso e satisfação dos cidadãos frente aos serviços de saúde do Sistema Único de Saúde (SUS), buscando a apreensão mais completa e abrangente possível da suas realidades, em suas diferentes dimensões, na busca da resolubilidade e qualidade. Art. 3º O PNASS tem como objetivos específicos: I - incentivar a cultura avaliativa dos gestores para os serviços de saúde; II - fomentar a cultura avaliativa nos estabelecimentos de saúde; III - ser instrumento de apoio à gestão do SUS; IV - produzir conhecimento qualitativo da rede de serviços de saúde; V - implementar padrões de conformidade dos serviços de saúde; VI - incorporar indicadores de produção para avaliação de serviços de saúde; VII - aferir a satisfação dos usuários do SUS; VIII - conhecer as condições e relações de trabalho dos profissionais nos estabelecimentos de saúde; IX - identificar oportunidades e possibilidades de melhoria; X - possibilitar a observação de experiências exitosas para melhoria da qualidade local; e XI - disponibilizar os resultados para conhecimento público. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2015/prt0028_08_01_2015.html', 'Programa com ação orçamentária associada', 'Portaria', 5, 1, 1, null, null, 'Sistema de Saúde');
