INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (611, 'Programa Nacional de Competitividade em Vacinas (INOVACINA)', '2006-01-01', 'Não se aplica', 'Não se aplica', '972/2006', '2006-05-03', null, 'Art. 2º  O Programa INOVACINA tem por finalidade criar condições para alcançar a auto-suficiência nacional na produção das vacinas incluídas no Programa Nacional de Imunização (PNI), por meio das seguintes ações: I - investir na infra-estrutura e no custeio da pesquisa e desenvolvimento em vacinas, incluindo bolsas de pesquisa; II - investir no estabelecimento de condições de Boas Práticas de Produção (BPP) nos laboratórios públicos e privados sem fins lucrativos, produtores de vacinas no País; III - aperfeiçoar o sistema de regulação de vacinas, com especial atenção aos mecanismos desenvolvidos pela Agência Nacional de Vigilância Sanitária (ANVISA); e IV - investir no estabelecimento de uma Rede Pública de Ensaios pré-clínicos e clínicos de vacinas, destinada a avaliar a segurança, a eficácia e efetividade das vacinas produzidas. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2006/prt0972_03_05_2006.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'Sistema de Saúde');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (612, 'Programa Nacional de Controle da Dengue (PNCD)', '2003-01-01', 'Não se aplica', 'Não se aplica', '1933/2003', '2003-10-09', null, 'Desenvolver ações permanentes de combate à dengue. Art. 1º Instituir o Programa Nacional de Controle da Dengue - PNCD, com as seguintes diretrizes: I - desenvolver campanhas de informação e de mobilização das pessoas, de maneira a criar-se uma maior responsabilização de cada família na manutenção de seu ambiente doméstico livre de potenciais criadouros do vetor; II - fortalecer a vigilância epidemiológica e entomológica para ampliar a capacidade de predição e de detecção precoce de surtos da doença; III - melhorar a qualidade do trabalho de campo de combate ao vetor; IV - integrar as ações de controle da dengue na atenção básica, com a mobilização do Programa de Agentes Comunitários de Saúde e Programa de Saúde da Família; V - utilizar instrumentos legais que facilitem o trabalho do poder público na eliminação de criadouros em imóveis comerciais, casas abandonadas, dentre outros; VI - atuar multisetorialmente por meio do fomento à destinação adequada de resíduos sólidos e a utilização de recipientes seguros para armazenagem de água; VI - desenvolver instrumentos mais eficazes de acompanhamento e supervisão das ações desenvolvidas pelo Ministério da Saúde, Estados e Municípios. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2003/prt1933_09_10_2003.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'População em geral');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (613, 'Programa Nacional de Controle de Qualidade Externo em Sorologia e lmunohematologia para Unidades Hemoterápicas (PNCQES)', '1996-01-01', 'Não se aplica', 'Não se aplica', '1840/1996', '1996-09-13', null, 'Portaria de Consolidação 5. ANEXO 12 DO ANEXO IV PROGRAMA NACIONAL DE CONTROLE DE QUALIDADE EXTERNO EM SOROLOGIA (PNCQES) (Origem: PRT MS/GM 1840/1996, Anexo 1) 1. Definições 1.1 Programa Nacional de Controle de Qualidade Externo em Sorologia - PNCQES - É um Programa de amplitude nacional, implantado pelo Ministério da Saúde, com o objetivo de avaliar a qualidade e o desempenho dos resultados doa testes sorológicos realizados nas Unidades Hemoterápicas. Esta avaliação será realizada no mínimo semestralmente, utilizando painéis de soros conhecidos (multipaineis), produzidos pela instituição credenciada. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/1996/prt1840_13_09_1996.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'Pessoas afetadas por doenças');
