INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (614, 'Programa Nacional de Controle e Eliminação da Hanseníase e de Outras Dermatoses de Interesse Sanitário', '2000-01-01', 'Não se aplica', 'Não se aplica', '814/2000', '2000-07-26', null, 'Art. 1º  Instituir, no âmbito do Sistema Único de Saúde (SUS), um conjunto de ações e medidas coordenadas para a eliminação da hanseníase, a serem promovidas de forma compartilhada entre o Ministério da Saúde e as Secretarias de Saúde dos Estados, dos Municípios e do Distrito Federal, com a finalidade de fortalecer o desenvolvimento das ações de prevenção, diagnóstico, tratamento, reabilitação e controle da hanseníase, tendo por objetivos: I - ampliar o acesso ao diagnóstico e ao tratamento nos municípios de maior endemicidade; II - garantir atenção integral aos pacientes em todos os níveis de complexidade; III - assegurar, nas unidades de saúde, tratamento completo para as formas paucibacilar e multibacilar; IV - desenvolver ações de promoção à saúde e vigilância epidemiológica, bem como de diagnóstico e acompanhamento, tratamento e reabilitação das pessoas atingidas pela doença; V - promover a organização, a regulação, o acompanhamento e a avaliação do conjunto de ações de saúde para a efetiva eliminação da hanseníase; VI - garantir informação acessível, atualizada e confiável, como ferramenta de avaliação, acompanhamento do estágio da eliminação para tomada de decisão; VII - responsabilizar os gestores e mobilizar a sociedade civil, na promoção do conhecimento sobre os sinais e sintomas da hanseníase; e VIII - ampliar a oferta de procedimentos de reabilitação física aos pacientes portadores de incapacidades/deformidades decorrentes da hanseníase. Art. 2º  Integrar as ações de eliminação da hanseníase – diagnóstico e tratamento – na Atenção Básica, com o objetivo de promover o maior acesso das pessoas atingidas pela doença, em especial nos Municípios endêmicos.', 'A Portaria http://www.credesh.ufu.br/sites/credesh.hc.ufu.br/arquivos/Portaria%20n814%20GM%20Em%2026%20de%20Julho%20de%202000.pdf menciona o Programa e a Portaria https://bvsms.saude.gov.br/bvs/saudelegis/gm/2004/prt0586_06_04_2004.html não o menciona, mas define os objetivos das ações de prevenção, diagnóstico, tratamento, reabilitação e controle da hanseníase.', '#N/A', 'Portaria', 5, 1, 1, null, null, 'População em geral');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (615, 'Programa Nacional de Desenvolvimento Gerencial no Sistema Único de Saúde (PNDG)', '2010-01-01', 'Não se aplica', 'Não se aplica', '1311/2010', '2010-05-27', null, 'Art. 1º Instituir o Programa Nacional de Desenvolvimento Gerencial no Sistema Único de Saúde - PNDG, com o objetivo de incentivar os processos de qualificação para a gestão e gerência do Sistema Único de Saúde. § 2º- São objetivos do PNDG: I - garantir a integralidade e a qualidade das práticas de saúde, no âmbito do SUS; II - qualificar os profissionais de saúde para o exercício da função gerencial em todos os pontos do SUS; III - estabelecer instrumentos de cooperação entre os gestores do SUS e as instituições de educação superior da área da saúde, visando à melhoria da qualidade e resolutividade da atenção à saúde prestada ao usuário e à integração da rede na educação permanente; IV - promover articulação entre as diversas iniciativas de formação e de capacitação dos gestores nos diversos níveis de governo; e V - buscar integração com as instituições de ensino, principalmente em relação às universidades, escolas de saúde pública, institutos de saúde coletiva. ', 'https://www.legisweb.com.br/legislacao/?id=228136', 'Ação 8630 (2008 a 2012)', 'Portaria', 5, 1, 1, null, null, 'Sistema de Saúde');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (616, 'Programa Nacional de Fomento à Produção Pública e Inovação no Complexo Industrial da Saúde', '2008-01-01', 'Não se aplica', 'Não se aplica', '374/2008', '2008-02-28', null, 'Art. 2º O Programa ora instituído tem como objetivo promover o fortalecimento e a modernização do conjunto de laboratórios públicos encarregados da produção de medicamentos e imunobiológicos de relevância estratégica para o Sistema Único de Saúde, por intermédio da ampliação da participação no Complexo Produtivo da Saúde, do aumento da capacidade inovadora e da mudança de patamar competitivo, contribuindo para a redução da defasagem tecnológica existente e o desenvolvimento econômico, científico e tecnológico do País. Art. 3º Estabelecer que os objetivos específicos do Programa, segundo os segmentos que compõem a produção do conjunto de laboratórios públicos, são: I - Segmento Farmacêutico: a) estimular a internalização da produção de farmoquímicose medicamentos de maior impacto para o Sistema Único de Saúde; b) modernizar os Laboratórios Farmacêuticos Oficiais em termos tecnológicos e organizacionais e capacitá-los para estabelecer parcerias com o setor produtivo privado; c) estimular a produção de medicamentos para doenças negligenciadas e de suas matérias-primas, superando o hiato entre o esforço de produção e inovação dos países desenvolvidos e as necessidades nacionais; e d) apoiar a implementação da Política Nacional de Plantas Medicinais e Fitoterápicos. II - Segmento Biotecnológico (imunobiológicos, biofármacos, hemoderivados e reagentes para diagnóstico): a) capacitar os produtores públicos do País para a produção da matéria-prima ativa com recursos às novas biotecnologias; b) modernizar os produtores públicos em termos tecnológicos e organizacionais e capacitá-los para estabelecer parcerias com o setor produtivo privado; c) estruturar o País para exportar produtos que atendam às demandas articuladas pela Organização Mundial da Saúde para as regiões menos desenvolvidas; d) implementar a Política de Desenvolvimento da Biotecnologia na área de Saúde Humana; e) priorizar a produção, no País, de reagentes para o diagnóstico de doenças transmissíveis utilizados pelo SUS com recursos às novas biotecnologias; f) consolidar a produção pública de reagentes biológicos para diagnóstico e estabelecer parcerias com o setor privado e instituições científicas para o desenvolvimento e a comercialização de novos produtos e insumos para diagnóstico; g) internalizar, no País, a produção de hemoderivados, atendendo aos preceitos constitucionais, associada com a capacitação tecnológica; h) favorecer a consolidação do produtor estatal nacional (Hemobras) como uma empresa pública eficiente, comprometida com resultados e tecnologicamente qualificada; e i) fomentar o desenvolvimento de hemoderivados produzidos por tecnologia recombinante e outras rotas tecnológicas. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2008/prt0374_28_02_2008.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'Sistema de Saúde');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (617, 'Programa Nacional de Genômica e Saúde de Precisão (Genomas Brasil)', '2020-01-01', 'Não se aplica', 'Não se aplica', '1949/2020', '2020-08-04', null, 'Art. 837-L. Fica instituído, no âmbito do Sistema Único de Saúde (SUS), o Programa Nacional de Genômica e Saúde de Precisão (Genomas Brasil). Parágrafo único. O Genomas Brasil é um programa de ciência, tecnologia e inovação que tem como finalidade: I - incentivar o desenvolvimento científico e tecnológico nacional nas áreas de genômica e saúde de precisão; II - promover o desenvolvimento da indústria genômica nacional; e III - estabelecer prova de conceito para uma linha de cuidado em genômica e saúde de precisão no âmbito do SUS. Art. 837- N. São objetivos do Programa Genomas Brasil: I - estabelecer o genoma de referência da população brasileira; II - instituir um banco nacional de dados genômicos e clínicos; III - promover o aumento da capacidade científica instalada e do capital intelectual nacional em medicina genômica e saúde de precisão; IV - promover o fortalecimento e a competitividade da indústria nacional de insumos e de produtos de medicina genômica e saúde de precisão; e V - capacitar a força de trabalho do SUS em medicina genômica e saúde de precisão. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2020/prt1949_05_08_2020.html', 'Indefinida', 'Portaria', 5, 1, 1, null, null, 'Sistema de Saúde');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (618, 'Programa Nacional de Gestão de Custos', '2018-01-01', 'Não se aplica', 'Não se aplica', '55/2018', '2018-01-12', null, 'Gerar e incentivar a efetiva utilização da informação de custos pelos gestores de saúde, no intuito de otimizar o desempenho do SUS', '#N/A', 'Programa sem ação orçamentária associada', 'Portaria', 5, 1, 1, null, null, 'Sistema de Saúde');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (619, 'Programa Nacional de Incentivo à Parceria entre os Hospitais Filantrópicos Sem Fins Lucrativos', '2002-01-01', 'Não se aplica', 'Não se aplica', '878/2002', '2001-08-30', null, 'Art. 1º Criar o Incentivo de Integração ao Sistema Único de Saúde – INTEGRASUS. § 1º O Incentivo de que trata este Artigo, a ser pago adicionalmente ao faturamento das entidades, destina-se, exclusivamente, aos hospitais filantrópicos e  sem fins lucrativos e tem por objetivo estimular o desenvolvimento de suas atividades assistenciais e a realização das mesmas em regime de parceria com o Poder Público; ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2002/prt0878_08_05_2002.html; https://bvsms.saude.gov.br/bvs/saudelegis/gm/2001/prt1413_30_08_2001.html; https://bvsms.saude.gov.br/bvs/saudelegis/gm/2001/prt0604_24_04_2001.html ', '#N/A', 'Portaria', 5, 1, 1, null, null, 'Sistema de Saúde');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (973, 'Programa de Desenvolvimento Científico e Tecnológico do Setor Espacial', '2000-01-01', 'Não se aplica', 'Não se aplica', '9994/2000', '2000-07-25', null, 'Art. 1o É instituído o Programa de Desenvolvimento Científico e Tecnológico do Setor Espacial, destinado ao fomento da atividade de pesquisa científica e desenvolvimento tecnológico do Setor Espacial', '#N/A', 'Programa com ação orçamentária associada', 'Lei ordinária', 5, 3, 9, null, null, 'Instituiçoes de pesquisa e inovação');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (621, 'Programa Nacional de Prevenção e Controle da Malária (PNCM)', '2003-01-01', 'Não se aplica', 'Não se aplica', '1932/2003', '2003-10-09', null, 'Art. 1º Instituir o Programa Nacional de Prevenção e Controle da Malária – PNCM, com as seguintes diretrizes: I - desenvolver atividades de informação e de mobilização político-social, com o objetivo de aumentar a participação da população nas ações de prevenção e controle da malária; II - fortalecer a vigilância em saúde para ampliar a capacidade de predição e de detecção precoce de surtos da doença; III - melhorar a qualidade do trabalho de campo no controle vetorial; IV - integrar as ações de controle da malária na atenção básica, com a efetiva participação dos Programas de Agentes Comunitários de Saúde e do Programa Saúde da Família; V - utilizar os instrumentos legais que facilitem o trabalho do poder público no controle do meio ambiente para evitar surtos da doença; VI - atuar com o Instituto Nacional de Colonização e Reforma Agrária – INCRA e o Instituto Brasileiro do Meio Ambiente e dos Recursos Naturais Renováveis – IBAMA, nas regiões endêmicas de malária, visando à promoção de ações de prevenção e controle da doença para evitar surgimento de epidemias decorrentes de atividades antrópicas; VII - desenvolver instrumentos de programação, acompanhamento e supervisão das ações desenvolvidas pelos gestores federal, estaduais e municipais.', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2003/prt1932_09_10_2003.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'População em geral');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (622, 'Programa Nacional de Prevenção e Controle dos Distúrbios por Deficiência de Iodo (DDI)', '2005-01-01', 'Não se aplica', 'Não se aplica', '2362/2005', '2005-12-01', null, 'Art. 2º  Definir que o Pró-Iodo destina-se a promover a eliminação virtual sustentável dos DDI mediante a obrigatoriedade de iodação do sal destinado ao consumo humano em todo o território nacional.', 'A portaria https://bvsms.saude.gov.br/bvs/saudelegis/gm/2005/prt2362_01_12_2005.html fala em reestruturação do Programa, mas este manual https://bvsms.saude.gov.br/bvs/publicacoes/manual_tecnico_operacional_proiodo.pdf fala em criação. A portaria revoga outra de 1994 (Art. 18  Fica revogada a Portaria nº 2.165/GM, de 29 de dezembro de 1994, publicada no Diário Oficial nº 1, de 2 de janeiro de 1995, Seção 1, página 45), mas não consegui encontrá-la. Portanto, o Programa pode ter começado no ano de 1994.', '#N/A', 'Portaria', 5, 1, 1, null, null, 'População em geral');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (623, 'Programa Nacional de Qualidade em Mamografia (PNQM)', '2012-01-01', 'Não se aplica', 'Não se aplica', '531/2012', '2012-11-29', null, 'Garantir a qualidade dos exames de mamografia oferecidos à população, minimizando-se o risco associado ao uso dos raios-X.', 'O programa foi atualizado pela Portaria 2898, de 28/11/2013, e incluído na Portaria de Consolidação nº 1, de 28 de setembro de 2017', 'Programa sem ação orçamentária associada', 'Portaria', 5, 1, 1, null, null, 'Sistema de Saúde');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (624, 'Programa Nacional de Qualificação da Assistência Farmacêutica (QUALIFAR/SUS)', '2012-01-01', 'Não se aplica', 'Não se aplica', '1214/2012', '2012-06-13', null, 'Art. 2º O QUALIFAR-SUS tem por finalidade contribuir para o processo de aprimoramento, implementação e integração sistêmica das atividades da Assistência Farmacêutica nas ações e serviços de saúde, visando a uma atenção contínua, integral, segura responsável e humanizada. Art. 4º O QUALIFAR-SUS está organizado em 4 (quatro) eixos, com os seguintes objetivos: I - Eixo Estrutura: contribuir para a estruturação dos serviços farmacêuticos no SUS, de modo que estes sejam compatíveis com as atividades desenvolvidas na Assistência Farmacêutica, considerando a área física, os equipamentos, mobiliários e recursos humanos; II - Eixo Educação: promover a educação permanente e capacitação dos profissionais de saúde para qualificação das ações da Assistência Farmacêutica voltadas ao aprimoramento das práticas profissionais no contexto das Redes de Atenção à Saúde; III - Eixo Informação: produzir documentos técnicos e disponibilizar informações que possibilitem o acompanhamento, monitoramento e avaliação das ações e serviços da Assistência Farmacêutica; e IV - Eixo Cuidado: inserir a Assistência Farmacêutica nas práticas clínicas visando a resolutividade das ações em saúde, otimizando os benefícios e minimizando os riscos relacionados à farmacoterapia. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2012/prt1214_13_06_2012.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'Pessoas afetadas por doenças');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (625, 'Programa Nacional de Qualificação para a Doação de Órgãos e de Tecidos para Transplantes', '2010-01-01', 'Não se aplica', 'Não se aplica', '2933/2010', '2010-09-28', null, 'Art. 1º Instituir, no âmbito do Sistema Nacional de Transplantes - SNT, o Programa Nacional de Qualificação para a Doação de Órgãos e Tecidos para Transplantes - QUALIDOTT. § 1º O Programa ora instituído, de forma integrada e sistêmica, estrutura e organiza as ações voltadas para a capacitação dos trabalhadores de saúde envolvidos no processo de doação/transplante desde a identificação de prováveis doadores de órgãos e de tecidos até o acompanhamento pós-transplante, e tem por objetivos: I - introduzir a capacitação, a qualificação e o processo de aprendizagem aplicados a uma abordagem sistêmica e organizada dos conhecimentos no processo de doação/transplante; II - proporcionar formação, capacitação, atualização e desenvolvimento dos profissionais envolvidos com o processo de doação/transplante; III - oferecer aos profissionais de saúde adequada informação e conhecimento da estrutura e funcionamento da Política Nacional de Transplantes; IV - estimular a pesquisa, a produção intelectual e a divulgação de conhecimentos, sensibilizando os profissionais para a importância do autodesenvolvimento e multiplicação do conhecimento; V - buscar maior integração dos profissionais envolvidos com as atividades de doação e de transplantes, com o objetivo de compartilhar experiências e conhecimentos; VI - motivar e incentivar a criatividade na busca por constantes estratégias de solução de problemas; e VII - contribuir para o aumento da doação e da captação de Órgãos, Tecidos e Células, por meio da qualificação profissional, com consequente aumento do número de transplantes realizados, visando à redução do tempo de espera em lista e à melhoria da qualidade de vida dos receptores.', 'A portaria de criação do programa foi consolidada na Portaria de Consolidação nº 4, de 28 de setembro de 2017', 'Programa sem ação orçamentária associada', 'Portaria', 5, 1, 1, null, null, 'Sistema de Saúde');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (626, 'Programa Nacional de Reorientação da Formação Profissional em Saúde (Pró-Saúde)', '2005-01-01', 'Não se aplica', 'Não se aplica', '2101/2005', '2005-11-04', null, 'Art. 1º Instituir o Programa Nacional de Reorientação da Formação Profissional em Saúde (Pró-Saúde) para os cursos de graduação em Medicina, Enfermagem e Odontologia, visando à necessidade de incentivar transformações do processo de formação, geração de conhecimentos e prestação de serviços à comunidade, para abordagem integral do processo de saúde-doença.', '#N/A', 'Programa sem ação orçamentária associada', 'Portaria', 5, 1, 1, null, null, 'Administração pública e servidores públicos');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (627, 'Programa Nacional de Segurança do Paciente', '2013-01-01', 'Não se aplica', 'Não se aplica', '529/2013', '2013-04-01', null, 'Art. 2º O PNSP tem por objetivo geral contribuir para a qualificação do cuidado em saúde em todos os estabelecimentos de saúde do território nacional. Art. 3º Constituem-se objetivos específicos do PNSP: I - promover e apoiar a implementação de iniciativas voltadas à segurança do paciente em diferentes áreas da atenção, organização e gestão de serviços de saúde, por meio da implantação da gestão de risco e de Núcleos de Segurança do Paciente nos estabelecimentos de saúde; II - envolver os pacientes e familiares nas ações de segurança do paciente; III - ampliar o acesso da sociedade às informações relativasà segurança do paciente; IV - produzir, sistematizar e difundir conhecimentos sobre segurança do paciente; e V - fomentar a inclusão do tema segurança do paciente no ensino técnico e de graduação e pós-graduação na área da saúde. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2013/prt0529_01_04_2013.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'Pessoas afetadas por doenças');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (630, 'Programa Nacional de Triagem Neonatal', '2001-01-01', 'Não se aplica', 'Não se aplica', '822/2001', '2001-06-06', null, 'Art. 1º § 1º O Programa ora instituído deve ser executado de forma articulada pelo Ministério da Saúde e pelas Secretarias de Saúde dos estados, Distrito Federal e municípios e tem por objetivo o desenvolvimento de ações de triagem neonatal em fase pré-sintomática, acompanhamento e tratamento das doenças congênitas detectadas inseridas no Programa em todos os nascidos-vivos, promovendo o acesso, o incremento da qualidade e da capacidade instalada dos laboratórios especializados e serviços de atendimento, bem como organizar e regular o conjunto destas ações de saúde;', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2001/prt0822_06_06_2001.html', 'Ação 8585 - PO 0005 FAEC', 'Portaria', 5, 1, 1, null, null, 'Gestantes, puérperas e recém-nascidos');
