INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (633, 'Programa Nacional para Qualificação, Produção e Inovação em Equipamentos e Materiais de Uso em Saúde no Complexo Industrial da Saúde', '2008-01-01', 'Não se aplica', 'Não se aplica', '375/2008', '2008-02-28', null, 'Art. 2º O Programa ora instituído tem como objetivo fortalecer e modernizar o setor de equipamentos e materiais de uso em saúde visando à ampliação da capacidade inovadora das empresas e a mudança de seu patamar competitivo, contribuindo para a redução da defasagem tecnológica existente e o desenvolvimento econômico, científico e tecnológico do País. Art. 3º Os objetivos específicos estabelecidos para o Pro-grama são: I -definir prioridades considerando as demandas tecnológicas do SUS, os mercados alvo do setor produtivo e a competência técnica instalada no País; II - estimular a interação entre empresas universidades e ICT''s com o setor produtivo e incentivar o intercâmbio tecnológico entre os setores industriais; III - articular para garantir estabilidade regulatória em par-ceria com a Anvisa e o Inmetro; IV - estimular a certificação de produtos como instrumento promotor da inovação e desenvolvimento do setor; V - estimular a ampliação dos investimentos públicos e privados em P,D&I.; VI - estimular o setor privado a utilizar os instrumentos de apoio existentes para ampliar o desenvolvimento tecnológico no setor público e privado; VII -estimular a formação e capacitação de recursos humanos nas áreas biomédicas; VIII - expandir e modernizar a infra-estrutura de Tecnologia Indústria Básica - TIB para o setor; e IX - propor a adoção de políticas de compra governamentais baseadas na qualidade dos insumos e na transferência de tecnologia. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2008/prt0375_28_02_2008.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'Instituiçoes de pesquisa e inovação');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (634, 'Programa Nacional Telessaúde Brasil Redes', '2011-01-01', 'Não se aplica', 'Não se aplica', '2546/2011', '2010-02-24', null, 'Art. 1º Esta Portaria redefine e amplia o Programa Telessaúde Brasil, que passa a ser denominado Programa Nacional Telessaúde Brasil Redes (Telessaúde Brasil Redes). Parágrafo único. O Telessaúde tem por objetivo apoiar a consolidação das Redes de Atenção à Saúde ordenadas pela Atenção Básica no âmbito do Sistema Único de Saúde (SUS).', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2011/prt2546_27_10_2011.html; https://bvsms.saude.gov.br/bvs/saudelegis/gm/2010/prt0402_24_02_2010_comp.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'Administração pública e servidores públicos');
