INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (949, 'Programa Bolsa Família', '2004-01-01', '132/2003', '21 de outubro de 2003', '10836/2004', '2004-01-09', null, 'Art. 1º Fica criado, no âmbito da Presidência da República, o Programa Bolsa Família, destinado às ações de transferência de renda com condicionalidades. Parágrafo único. O Programa de que trata o caput tem por finalidade a unificação dos procedimentos de gestão e execução das ações de transferência de renda do Governo Federal, especialmente as do Programa Nacional de Renda Mínima vinculado à Educação - Bolsa Escola, instituído pela Lei nº 10.219, de 11 de abril de 2001, do Programa Nacional de Acesso à Alimentação - PNAA, criado pela Lei n o 10.689, de 13 de junho de 2003, do Programa Nacional de Renda Mínima vinculada à Saúde - Bolsa Alimentação, instituído pela Medida Provisória n o 2.206-1, de 6 de setembro de 2001, do Programa Auxílio-Gás, instituído pelo Decreto nº 4.102, de 24 de janeiro de 2002, e do Cadastramento Único do Governo Federal, instituído pelo Decreto nº 3.877, de 24 de julho de 2001.', '#N/A', 'Programa com ação orçamentária associada', 'Lei ordinária', 5, 1, 6, null, null, 'População em situação de vulnerabilidade social');
