INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (1150, 'Política Nacional de Atenção Integral em Reprodução Humana Assistida', '2005-01-01', 'Não se aplica', 'Não se aplica', '426/2005', '2005-03-22', null, 'Art. 2°  Determinar que a Política Nacional de Atenção Integral em Reprodução Humana Assistida seja implantada de forma articulada entre o Ministério da Saúde, as Secretarias de Estado de Saúde e as Secretarias Municipais de Saúde, permitindo: I - organizar uma linha de cuidados integrais (promoção, prevenção, tratamento e reabilitação) que perpasse todos os níveis de atenção, promovendo, dessa forma, a atenção por intermédio de equipe multiprofissional, com atuação interdisciplinar; II - identificar os determinantes e condicionantes dos principais problemas de infertilidade em casais em sua vida fértil, e desenvolver ações transetoriais de responsabilidade pública, sem excluir as responsabilidades de toda a sociedade; III - definir critérios técnicos mínimos para o funcionamento, o monitoramento e a avaliação dos serviços que realizam os procedimentos e técnicas de reprodução humana assistida, necessários à viabilização da concepção, tanto para casais com infertilidade, como para aqueles que se beneficiem desses recursos para o controle da transmissão vertical e/ou horizontal de doenças; IV - fomentar, coordenar e executar projetos estratégicos que visem ao estudo do custo-efetividade, eficácia e qualidade, bem como a incorporação tecnológica na área da reprodução humana assistida no Brasil; V - promover intercâmbio com outros subsistemas de informações setoriais, implementando e aperfeiçoando permanentemente a produção de dados e garantindo a democratização das informações; e VI - qualificar a assistência e promover a educação permanente dos profissionais de saúde envolvidos com a implantação e a implementação da Política de Atenção Integral em Reprodução Humana Assistida, em conformidade com os princípios da integralidade e da Política Nacional de Humanização - PNH.', '#N/A', 'Política sem ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'Pessoas afetadas por doenças');
