INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (1185, 'Programa de Atenção Integral à Família (Paif)', '2004-01-01', 'Não se aplica', 'Não se aplica', '78/2004', '2004-04-08', null, 'Art.  2º  -  O  Programa  de  Atenção  Integral  à  Família  -  PAIF tem  como  objetivos: I.  contribuir  para  a  efetivação  da  Política  de  Assistência  Social  como  política  pública  garantidora  de  direitos  de cidadania  e  promotora  de  desenvolvimento  social,  na  perspectiva  da  prevenção  e  superação  das  desigualdades  e exclusão  social,  tendo  a  família  como  unidade  de  atenção  para  a  concepção  e  a  implementação  de  programas,  projetos, serviços  e  benefícios. II.  contribuir  para  superar  a  abordagem  fragmentada  e  individualizadora  dos  programas  tradicionais; III.  garantir  a  convivência  familiar  e  comunitária  dos  membros  das famílias; IV.  contribuir  para  o  processo  de  autonomia  e  emancipação  social  das famílias  e  seus  membros; V.  viabilizar  a  formação  para  a  cidadania; VI.  articular  e  integrar  ações públicas e  privadas em  rede; VII.  colaborar  com  a  descentralização  político-administrativa.', '#N/A', '#N/A', 'Portaria', 5, 1, 6, null, null, 'População em situação de vulnerabilidade social');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (1199, 'Programa de Governo Eletrônico - Serviço de Atendimento ao Cidadão (GESAC)', '2002-01-01', 'Não se aplica', 'Não se aplica', '256/2002', '2002-03-13', null, 'Art. 1º Definir o Programa GESAC - Governo Eletrônico - Serviço de Atendimento ao Cidadão, que trata da Universalização do Acesso à Internet, com o objetivo de disseminar meios que permitam a universalização do acesso às informações e serviços do governo, por meio eletrônico.', '#N/A', '#N/A', 'Portaria', 5, 6, 8, null, null, 'População em geral');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (1186, 'Programa de Atenção Integral a Usuários de Álcool e outras Drogas', '2004-01-01', 'Não se aplica', 'Não se aplica', '2197/2004', '2004-10-14', null, 'Art. 5º  O componente de atenção hospitalar de referência, objeto do inciso III, do artigo 2º desta Portaria, define que os Serviços Hospitalares de Referência para a Atenção Integral aos Usuários de Álcool e outras Drogas - SHR-ad serão instalados em Hospitais Gerais, e têm como objetivos: I - compor rede de atenção integral a usuários de álcool e outras drogas, participando do sistema de organização e regulação das demandas e fluxos assistenciais, em área geográfica definida, respeitando as atribuições e competências das instâncias do SUS para a sua implantação e gerenciamento; II - compor, na rede assistencial, e em sua estrutura de atendimento hospitalar de urgência e emergência, a rede hospitalar de retaguarda aos usuários de álcool e outras drogas; III - atuar respeitando as premissas do SUS e a lógica territorial, salvo em casos de ausência de recursos assistenciais similares, onde a clientela atendida poderá ultrapassar os limites territoriais previstos para a abrangência do serviço; IV - dar suporte à demanda assistencial caracterizada por situações de urgência/emergência que sejam decorrentes do consumo ou abstinência de álcool e/ou outras drogas, advindas da rede dos Centros de Atenção Psicossocial para a Atenção a Usuários de Álcool e outras Drogas (CAPSad), da rede básica de cuidados em saúde (Programa Saúde da Família, e Unidades Básicas de Saúde), e de serviços ambulatoriais especializados e não-especializados; V - oferecer suporte hospitalar, por meio de internações de curta duração para usuários de álcool e/ou outras drogas, em situações assistenciais para as quais os recursos extra-hospitalares disponíveis não tenham obtido a devida resolutividade, ou ainda em casos de necessidade imediata de intervenção em ambiente hospitalar, sempre respeitadas as determinações da Lei nº 10.216, e sempre acolhendo os pacientes em regime de curtíssima e curta permanência; VI - oferecer, nas situações descritas nos incisos III e IV, do artigo 2° desta Portaria, abordagem, suporte e encaminhamento adequado aos usuários que, mediante avaliação geral, evidenciarem indicativos de ocorrência de comorbidades de ordem clínica e/ou psíquica; e VII - evitar a internação de usuários de álcool e outras drogas em hospitais psiquiátricos. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2004/prt2197_14_10_2004.html; A PT faz referência aos seguintes componentes do Programa: I - componente da atenção básica; II - componente da atenção nos CAPS-AD, ambulatórios e outras unidades extra-hospitalares especializadas; III - componente da atenção hospitalar de referência; e IV - componente da rede de suporte social (associações de ajuda mútua e entidades da sociedade civil), complementar à rede de serviços disponibilizados pelo SUS. Contudo, são descritos objetivos apenas para o componente III - componente da atenção hospitalar de referência. ', 'Ação 20B0 para implantação dos CAPS (2008-2018) e 8585 (MAC) para custeio', 'Portaria', 5, 1, 1, null, null, 'Pessoas afetadas por doenças');
