INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (1254, 'Programa para o Desenvolvimento do Complexo Industrial da Saúde (PROCIS)', '2012-01-01', 'Não se aplica', 'Não se aplica', '506/2012', '2012-03-21', null, 'Art. 1º Fica criado o Programa para o Desenvolvimento do Complexo Industrial da Saúde (PROCIS), com o objetivo de fortalecer os produtores públicos e a infraestrutura de produção e inovação em saúde do setor público. Parágrafo único. Para efeitos desta Portaria são considerados produtores públicos os órgãos ou entidades que integrem a administração pública e sejam responsáveis pela produção de fármacos, biofármacos, medicamentos, imunobiológicos, produtos médicos, equipamentos e materiais de uso em saúde e "kits" para diagnóstico de uso "in vitro", primordialmente destinados aos programas estratégicos de saúde pública. Art. 2º O PROCIS tem os seguintes objetivos: I - apoiar a modernização e estruturação produtiva e gerencial; II - apoiar a qualificação da produção e manter vigente o Certificado de Boas Práticas de Fabricação (CBPF) emitido pela Agência Nacional de Vigilância Sanitária (ANVISA); III - fortalecer as parcerias para o desenvolvimento produtivo visando desenvolver e absorver produtos estratégicos para o Sistema Único de Saúde; IV - apoiar a qualificação da gestão com vistas a promover maior eficiência e efetividade; V - apoiar o desenvolvimento tecnológico e a transferência de tecnologias, estratégicos para o Sistema Único de Saúde (SUS); VI - manter o aproveitamento das complementariedades entre os laboratórios e respeitar as vocações e o perfil produtivo; e VII - apoiar a infraestrutura pública de tecnologia e inovação para suporte à produção no país de produtos estratégicos para o SUS. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2012/prt0506_21_03_2012.html', 'Programa com ação orçamentária associada', 'Portaria', 5, 1, 1, null, null, 'Sistema de Saúde');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (1255, 'Programa Permanente de Capacitação de Recursos Humanos da rede SUS para os Serviços de Atenção aos Pacientes com Transtornos causados pelo Uso Prejudicial e/ou Dependência de Álcool e Outras Drogas', '2002-01-01', 'Não se aplica', 'Não se aplica', '816/2002', '2002-04-30', null, 'Capacitação de Recursos Humanos da rede SUS para os Serviços de Atenção aos Pacientes com Transtornos causados pelo Uso Prejudicial e/ou Dependência de Álcool e Outras Drogas', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2002/prt0816_30_04_2002.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'Administração pública e servidores públicos');
