INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (1256, 'Programa Permanente de Organização e Acompanhamento das Ações Assistenciais em Saúde Mental', '2000-01-01', 'Não se aplica', 'Não se aplica', '799/2000', '2000-07-19', null, 'Art. 1º Instituir, no âmbito do Sistema Único de Saúde, o Programa Permanente de Organização e Acompanhamento das Ações Assistenciais em Saúde Mental. Parágrafo único. O Programa de que trata este Artigo deverá contemplar as seguintes atividades: a - avaliação da assistência à saúde mental no País, dentro do contexto de sistema global de atenção, promoção e recuperação da saúde mental; b - supervisão e avaliação, in loco, dos hospitais psiquiátricos que compõem a rede assistencial do Sistema Único de Saúde, tendo em vista a adoção de medidas que reforcem a continuidade do processo de reversão do modelo de atenção à saúde mental instituído no País; c - análise, avaliação e proposição de alternativas assistenciais na área de saúde mental; d - elaboração e proposição de protocolos de regulação e de mecanismos de implementação de módulo de regulação da assistência à saúde mental que venha a compor as Centrais de Regulação, com o objetivo de regular, ordenar e orientar esta assistência e com o princípio fundamental de incrementar a capacidade do poder público de gerir o sistema de saúde e de responder, de forma qualificada e integrada, às demandas de saúde de toda a população. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2000/prt0799_19_07_2000.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'Sistema de Saúde');
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao) VALUES (1257, 'Programa Pesquisa para o SUS: gestão compartilhada em saúde (PPSUS)', '2019-01-01', 'Não se aplica', 'Não se aplica', '3020/2019', '2019-11-19', null, 'Art. 837-F. São objetivos do PPSUS: I - apoiar financeiramente o desenvolvimento de pesquisas científicas, tecnológicas e/ou de inovação que objetivem contribuir para a resolução de problemas de saúde da população dos Estados e do Distrito Federal e para o fortalecimento da gestão do SUS; II - promover a aproximação dos sistemas de saúde com os de ciência e tecnologia locais; e III - reduzir as desigualdades regionais no campo da ciência, tecnologia e inovação em saúde no País. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2019/prt3020_25_11_2019.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'Instituiçoes de pesquisa e inovação');
