INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (675, 'Política de Ciência, Tecnologia e Inovação de Defesa', '2021-01-01', 'Não se aplica', 'Não se aplica', '3063/2021', '2021-07-22', null, 'Art. 2º A Política de Ciência, Tecnologia e Inovação de Defesa tem como objetivo geral estimular o desenvolvimento científico e tecnológico e a inovação de interesse de Defesa. Art. 3º São objetivos específicos da Política de Ciência, Tecnologia e Inovação de Defesa: I - ampliar o conteúdo tecnológico dos produtos e serviços de interesse de Defesa; II - elevar o nível de capacitação de recursos humanos, vinculados ou dedicados à ciência, tecnologia e inovação de interesse de Defesa; III - aprimorar a infraestrutura de ciência, tecnologia e inovação de apoio a programas e projetos prioritários de interesse de Defesa; IV - proporcionar a criação de ambiente favorável à inovação, à cooperação, à competitividade industrial e à capacitação nacional, voltados aos interesses de Defesa; V - aperfeiçoar os mecanismos de financiamento das atividades de ciência, tecnologia e inovação de interesse de Defesa; VI - promover a cultura de Ciência, Tecnologia e Inovação de interesse da Defesa, junto a diversos segmentos da sociedade; VII - estimular o aproveitamento da imagem de excelência institucional; VIII - integrar as iniciativas de ciência, tecnologia e inovação de interesse de Defesa, conduzidas nas Instituições Científicas, Tecnológicas e de Inovação (ICT), nas empresas e na academia; IX - estabelecer mecanismos voltados a políticas para a valorização de recursos humanos baseada em resultados; e X - implantar sistemática que integre o planejamento estratégico, o ciclo de vida de produtos e serviços de interesse de Defesa e