INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (625, 'Programa Nacional de Qualificação para a Doação de Órgãos e de Tecidos para Transplantes', '2010-01-01', 'Não se aplica', 'Não se aplica', '2933/2010', '2010-09-28', null, 'Art. 1º Instituir, no âmbito do Sistema Nacional de Transplantes - SNT, o Programa Nacional de Qualificação para a Doação de Órgãos e Tecidos para Transplantes - QUALIDOTT. § 1º O Programa ora instituído, de forma integrada e sistêmica, estrutura e organiza as ações voltadas para a capacitação dos trabalhadores de saúde envolvidos no processo de doação/transplante desde a identificação de prováveis doadores de órgãos e de tecidos até o acompanhamento pós-transplante, e tem por objetivos: I - introduzir a capacitação, a qualificação e o processo de aprendizagem aplicados a uma abordagem sistêmica e organizada dos conhecimentos no processo de doação/transplante; II - proporcionar formação, capacitação, atualização e desenvolvimento dos profissionais envolvidos com o processo de doação/transplante; III - oferecer aos profissionais de saúde adequada informação e conhecimento da estrutura e funcionamento da Política Nacional de Transplantes; IV - estimular a pesquisa, a produção intelectual e a divulgação de conhecimentos, sensibilizando os profissionais para a importância do autodesenvolvimento e multiplicação do conhecimento; V - buscar maior integração dos profissionais envolvidos com as atividades de doação e de transplantes, com o objetivo de compartilhar experiências e conhecimentos; VI - motivar e incentivar a criatividade na busca por constantes estratégias de solução de problemas; e VII - contribuir para o aumento da doação e da captação de Órgãos, Tecidos e Células, por meio da qualificação profissional, com consequente aumento do número de transplantes realizados, visando à redução do tempo de espera em lista e à melhoria da qualidade de vida dos receptores.', 'A portaria de criação do programa foi consolidada na Portaria de Consolidação nº 4, de 28 de setembro de 2017', 'Programa sem ação orçamentária associada', 'Portaria', 5, 1, 1, null, null, 'Sistema Único de Saúde', null);
