INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (224, 'Política Nacional de Metrologia', '1967-01-01', 'Não se aplica', 'Não se aplica', '240/1967', '1967-02-28', null, 'Art. 2º A fim de assegurar em todo o território nacional a indispensável uniformidade na expressão quantitativa e metrológica das grandezas, cabe privativamente à União, conforme estabelecido na Constituição Federal: a) a definição das unidades legais de medir; b) a legislação sôbre tudo quanto se referir a essas unidades, ao seu emprêgo e, de modo geral, ao aspecto metrológico de quaisquer atividades comerciais, indústriais, técnicas ou científicas; c) a execução, diretamente, ou por meio de delegações, das atividades metrológicas; d) a fixação e a forma do recebimento das importâncias correspondes aos preços dos serviços efetuados em verificação de medidas e instrumentos de medir, e das multas previstas neste Decreto-lei.', '#N/A', 'Política com ação orçamentária associada', 'Decreto-Lei', 4, 3, 12, null, null, 'Metrologia', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (431, 'Programa Universidade para Todos (PROUNI)', '2005-01-01', '213/2004', '13 de setembro de 2004', '11096/2005', '2005-01-13', '2004-12-09', 'Art. 1º Fica instituído, sob a gestão do Ministério da Educação, o Programa Universidade para Todos - PROUNI, destinado à concessão de bolsas de estudo integrais e bolsas de estudo parciais de 50% (cinqüenta por cento) ou de 25% (vinte e cinco por cento) para estudantes de cursos de graduação e seqüenciais de formação específica, em instituições privadas de ensino superior, com ou sem fins lucrativos.', '#N/A', 'Programa com ação orçamentária associada', 'Lei ordinária', 5, 1, 32, null, '2023-03-08 12:14:23', '§ 1º A bolsa de estudo integral será concedida a brasileiros não portadores de diploma de curso superior, cuja renda familiar mensal per capita não exceda o valor de até 1 (um) salário-mínimo e 1/2 (meio).<br />§ 2º As bolsas de estudo parciais de 50% (cinqüenta por cento) ou de 25% (vinte e cinco por cento), cujos critérios de distribuição serão definidos em regulamento pelo Ministério da Educação, serão concedidas a brasileiros não-portadores de diploma de curso superior, cuja renda familiar mensal per capita não exceda o valor de até 3 (três) salários-mínimos, mediante critérios definidos pelo Ministério da Educação. (...)<br />Art. 2º A bolsa será destinada:<br />I - a estudante que tenha cursado o ensino médio completo em escola da rede pública ou em instituições privadas na condição de bolsista integral;<br />II - a estudante portador de deficiência, nos termos da lei;<br />III - a professor da rede pública de ensino, para os cursos de licenciatura, normal superior e pedagogia, destinados à formação do magistério da educação básica, independentemente da renda a que se referem os §§ 1º e 2º do art. 1º desta Lei.', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (786, 'Programa Pró-Equidade', '2022-01-01', 'Não se aplica', 'Não se aplica', '2467/2022', '2022-11-23', null, '<p><span data-sheets-value="{&quot;1&quot;:2,&quot;2&quot;:&quot;Art. 1&ordm; Instituir o Programa Pr&oacute;-Equidade no &acirc;mbito do Minist&eacute;rio da Mulher, da Fam&iacute;lia e dos Direitos Humanos, que tem os seguintes objetivos:\n\nI - conscientizar e incentivar empregadores em rela&ccedil;&atilde;o &agrave;s pr&aacute;ticas de gest&atilde;o de pessoas e de cultura organizacional que promovam a igualdade de oportunidades para todos, sem distin&ccedil;&otilde;es por sexo, ra&ccedil;a, etnia, dentre outros aspectos, mitigando todas as formas de discrimina&ccedil;&atilde;o na ascens&atilde;o funcional, no plano de cargo e de carreira, no sal&aacute;rio e na remunera&ccedil;&atilde;o;\n\nII - reconhecer publicamente o compromisso das empresas/institui&ccedil;&otilde;es com a igualdade entre mulheres e homens, independente de ra&ccedil;a, etnia, condi&ccedil;&atilde;o socioecon&ocirc;mica, dentre outros aspectos, no mundo do trabalho;\n\nIII - estimular o recrutamento e a sele&ccedil;&atilde;o sem distin&ccedil;&otilde;es por sexo, ra&ccedil;a, etnia, dentre outros aspectos, em todos os n&iacute;veis, nas empresas/institui&ccedil;&otilde;es, at&eacute; mesmo em profiss&otilde;es tradicionalmente ocupadas pelos homens;\n\nIV - incentivar a cria&ccedil;&atilde;o de boas pr&aacute;ticas e de benef&iacute;cios para as mulheres lactantes das empresas/institui&ccedil;&otilde;es, bem como promover pol&iacute;ticas de corresponsabiliza&ccedil;&atilde;o pelo cuidado dos filhos e dependentes (por meio, por exemplo, da possibilidade de licen&ccedil;a parental, trabalho flex&iacute;vel e aux&iacute;lios para servi&ccedil;os de aten&ccedil;&atilde;o &agrave; inf&acirc;ncia e &agrave; fam&iacute;lia);\n\nV - mapear programas de sa&uacute;de e de seguran&ccedil;a no trabalho das empresas/institui&ccedil;&otilde;es participantes do Programa;\n\nVI - estimular o oferecimento, por parte das empresa/institui&ccedil;&otilde;es, de um canal id&ocirc;neo aos colaboradores para den&uacute;ncias de pr&aacute;ticas contr&aacute;rias &agrave; dignidade da pessoa humana no ambiente de trabalho;\n\nVII - monitorar e incentivar que as empresas/institui&ccedil;&otilde;es cumpram as cotas de vagas disponibilizadas em lei para contrata&ccedil;&atilde;o de for&ccedil;a de trabalho; e\n\nVIII - promover iniciativas que se relacionem &agrave; preven&ccedil;&atilde;o e &agrave; repress&atilde;o de pr&aacute;ticas de racismo ou de discrimina&ccedil;&atilde;o no &acirc;mbito do trabalho.&quot;}" data-sheets-userformat="{&quot;2&quot;:15235,&quot;3&quot;:{&quot;1&quot;:0},&quot;4&quot;:{&quot;1&quot;:2,&quot;2&quot;:16776960},&quot;10&quot;:0,&quot;11&quot;:0,&quot;12&quot;:0,&quot;14&quot;:{&quot;1&quot;:2,&quot;2&quot;:0},&quot;15&quot;:&quot;Calibri, sans-serif&quot;,&quot;16&quot;:12}">Art. 1&ordm; Instituir o Programa Pr&oacute;-Equidade no &acirc;mbito do Minist&eacute;rio da Mulher, da Fam&iacute;lia e dos Direitos Humanos, que tem os seguintes objetivos:<br /><br />I - conscientizar e incentivar empregadores em rela&ccedil;&atilde;o &agrave;s pr&aacute;ticas de gest&atilde;o de pessoas e de cultura organizacional que promovam a igualdade de oportunidades para todos, sem distin&ccedil;&otilde;es por sexo, ra&ccedil;a, etnia, dentre outros aspectos, mitigando todas as formas de discrimina&ccedil;&atilde;o na ascens&atilde;o funcional, no plano de cargo e de carreira, no sal&aacute;rio e na remunera&ccedil;&atilde;o;<br /><br />II - reconhecer publicamente o compromisso das empresas/institui&ccedil;&otilde;es com a igualdade entre mulheres e homens, independente de ra&ccedil;a, etnia, condi&ccedil;&atilde;o socioecon&ocirc;mica, dentre outros aspectos, no mundo do trabalho;<br /><br />III - estimular o recrutamento e a sele&ccedil;&atilde;o sem distin&ccedil;&otilde;es por sexo, ra&ccedil;a, etnia, dentre outros aspectos, em todos os n&iacute;veis, nas empresas/institui&ccedil;&otilde;es, at&eacute; mesmo em profiss&otilde;es tradicionalmente ocupadas pelos homens;<br /><br />IV - incentivar a cria&ccedil;&atilde;o de boas pr&aacute;ticas e de benef&iacute;cios para as mulheres lactantes das empresas/institui&ccedil;&otilde;es, bem como promover pol&iacute;ticas de corresponsabiliza&ccedil;&atilde;o pelo cuidado dos filhos e dependentes (por meio, por exemplo, da possibilidade de licen&ccedil;a parental, trabalho flex&iacute;vel e aux&iacute;lios para servi&ccedil;os de aten&ccedil;&atilde;o &agrave; inf&acirc;ncia e &agrave; fam&iacute;lia);<br /><br />V - mapear programas de sa&uacute;de e de seguran&ccedil;a no trabalho das empresas/institui&ccedil;&otilde;es participantes do Programa;<br /><br />VI - estimular o oferecimento, por parte das empresa/institui&ccedil;&otilde;es, de um canal id&ocirc;neo aos colaboradores para den&uacute;ncias de pr&aacute;ticas contr&aacute;rias &agrave; dignidade da pessoa humana no ambiente de trabalho;<br /><br />VII - monitorar e incentivar que as empresas/institui&ccedil;&otilde;es cumpram as cotas de vagas disponibilizadas em lei para contrata&ccedil;&atilde;o de for&ccedil;a de trabalho; e<br /><br />VIII - promover iniciativas que se relacionem &agrave; preven&ccedil;&atilde;o e &agrave; repress&atilde;o de pr&aacute;ticas de racismo ou de discrimina&ccedil;&atilde;o no &acirc;mbito do trabalho.</span></p>', null, 'Não se aplica', 'Portaria', 5, 1, 3, '2023-04-23 18:22:47', '2023-04-23 18:22:47', '<p><span data-sheets-value="{&quot;1&quot;:2,&quot;2&quot;:&quot;Art. 2&ordm; O Programa Pr&oacute;-Equidade se destina a institui&ccedil;&otilde;es p&uacute;blicas e privadas de m&eacute;dio e grande portes, com personalidade jur&iacute;dica pr&oacute;pria e que n&atilde;o estejam contempladas pela Lei Complementar n&ordm; 123, de 14 de dezembro de 2016.&quot;}" data-sheets-userformat="{&quot;2&quot;:15235,&quot;3&quot;:{&quot;1&quot;:0},&quot;4&quot;:{&quot;1&quot;:2,&quot;2&quot;:16776960},&quot;10&quot;:0,&quot;11&quot;:0,&quot;12&quot;:0,&quot;14&quot;:{&quot;1&quot;:2,&quot;2&quot;:0},&quot;15&quot;:&quot;Calibri, sans-serif&quot;,&quot;16&quot;:12}">Art. 2&ordm; O Programa Pr&oacute;-Equidade se destina a institui&ccedil;&otilde;es p&uacute;blicas e privadas de m&eacute;dio e grande portes, com personalidade jur&iacute;dica pr&oacute;pria e que n&atilde;o estejam contempladas pela Lei Complementar n&ordm; 123, de 14 de dezembro de 2016.</span></p>', null);
