INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (785, 'Programa Nacional Qualifica Mulher', '2022-01-01', 'Não se aplica', 'Não se aplica', '11309/2022', '2022-12-26', null, '<p><span data-sheets-value="{&quot;1&quot;:2,&quot;2&quot;:&quot;Art. 2&ordm; S&atilde;o objetivos do Programa Nacional Qualifica Mulher:\n\nI - desenvolver, de forma transversal, a&ccedil;&otilde;es de educa&ccedil;&atilde;o profissional, a fim de aumentar a empregabilidade de mulheres em situa&ccedil;&atilde;o de vulnerabilidade e sua capacidade para o exerc&iacute;cio de qualquer trabalho;\n\nII - acompanhar e estimular o desenvolvimento de pol&iacute;ticas voltadas &agrave; amplia&ccedil;&atilde;o de direitos sociais ligados &agrave; proje&ccedil;&atilde;o econ&ocirc;mica das mulheres, especialmente daquelas em situa&ccedil;&atilde;o de vulnerabilidade;\n\nIII - promover a&ccedil;&otilde;es que contribuam para a:\n\na) valoriza&ccedil;&atilde;o do direito das mulheres ao trabalho, &agrave; cidadania, &agrave; dignidade e ao respeito; e\n\nb) mitiga&ccedil;&atilde;o da viol&ecirc;ncia e da discrimina&ccedil;&atilde;o contra as mulheres;\n\nIV - promover medidas que contribuam para o desenvolvimento e para a sustentabilidade financeira de mulheres em situa&ccedil;&atilde;o de vulnerabilidade, com vistas &agrave; garantia de sua proje&ccedil;&atilde;o econ&ocirc;mica;\n\nV - fomentar a inser&ccedil;&atilde;o e a reinser&ccedil;&atilde;o de mulheres m&atilde;es no mercado de trabalho, a concilia&ccedil;&atilde;o entre trabalho e fam&iacute;lia e a equidade e corresponsabilidade no lar; e\n\nVI - contribuir para iniciativas destinadas &agrave; amplia&ccedil;&atilde;o da oferta de microcr&eacute;dito para o empreendedorismo feminino, por meio da articula&ccedil;&atilde;o com &oacute;rg&atilde;os, entidades e institui&ccedil;&otilde;es, p&uacute;blicos e privados, desenvolvidas pela Estrat&eacute;gia Nacional de Empreendedorismo Feminino - Brasil para Elas, nos termos do disposto no Decreto n&ordm; 10.988, de 8 de mar&ccedil;o de 2022.&quot;}" data-sheets-userformat="{&quot;2&quot;:15235,&quot;3&quot;:{&quot;1&quot;:0},&quot;4&quot;:{&quot;1&quot;:2,&quot;2&quot;:16776960},&quot;10&quot;:0,&quot;11&quot;:0,&quot;12&quot;:0,&quot;14&quot;:{&quot;1&quot;:2,&quot;2&quot;:0},&quot;15&quot;:&quot;Calibri, sans-serif&quot;,&quot;16&quot;:12}">Art. 2&ordm; S&atilde;o objetivos do Programa Nacional Qualifica Mulher:<br /><br />I - desenvolver, de forma transversal, a&ccedil;&otilde;es de educa&ccedil;&atilde;o profissional, a fim de aumentar a empregabilidade de mulheres em situa&ccedil;&atilde;o de vulnerabilidade e sua capacidade para o exerc&iacute;cio de qualquer trabalho;<br /><br />II - acompanhar e estimular o desenvolvimento de pol&iacute;ticas voltadas &agrave; amplia&ccedil;&atilde;o de direitos sociais ligados &agrave; proje&ccedil;&atilde;o econ&ocirc;mica das mulheres, especialmente daquelas em situa&ccedil;&atilde;o de vulnerabilidade;<br /><br />III - promover a&ccedil;&otilde;es que contribuam para a:<br /><br />a) valoriza&ccedil;&atilde;o do direito das mulheres ao trabalho, &agrave; cidadania, &agrave; dignidade e ao respeito; e<br /><br />b) mitiga&ccedil;&atilde;o da viol&ecirc;ncia e da discrimina&ccedil;&atilde;o contra as mulheres;<br /><br />IV - promover medidas que contribuam para o desenvolvimento e para a sustentabilidade financeira de mulheres em situa&ccedil;&atilde;o de vulnerabilidade, com vistas &agrave; garantia de sua proje&ccedil;&atilde;o econ&ocirc;mica;<br /><br />V - fomentar a inser&ccedil;&atilde;o e a reinser&ccedil;&atilde;o de mulheres m&atilde;es no mercado de trabalho, a concilia&ccedil;&atilde;o entre trabalho e fam&iacute;lia e a equidade e corresponsabilidade no lar; e<br /><br />VI - contribuir para iniciativas destinadas &agrave; amplia&ccedil;&atilde;o da oferta de microcr&eacute;dito para o empreendedorismo feminino, por meio da articula&ccedil;&atilde;o com &oacute;rg&atilde;os, entidades e institui&ccedil;&otilde;es, p&uacute;blicos e privados, desenvolvidas pela Estrat&eacute;gia Nacional de Empreendedorismo Feminino - Brasil para Elas, nos termos do disposto no Decreto n&ordm; 10.988, de 8 de mar&ccedil;o de 2022.</span></p>', null, 'Não se aplica', 'Decreto', 5, 1, 6, '2023-04-23 18:13:26', '2023-04-23 18:13:26', '<p><span data-sheets-value="{&quot;1&quot;:2,&quot;2&quot;:&quot;O Programa Nacional Qualifica Mulher atender&aacute;, prioritariamente, mulheres que:\n\nI - possuam renda mensal de at&eacute; um sal&aacute;rio m&iacute;nimo e meio;\n\nII - tenham o ensino fundamental incompleto; e\n\nIII - sejam v&iacute;timas de viol&ecirc;ncia dom&eacute;stica, em conson&acirc;ncia com o Plano Nacional de Enfrentamento ao Feminic&iacute;dio, nos termos do disposto no Decreto n&ordm; 10.906, de 20 de dezembro de 2021, e com outras pol&iacute;ticas de enfrentamento &agrave; viol&ecirc;ncia contra a mulher.&quot;}" data-sheets-userformat="{&quot;2&quot;:15235,&quot;3&quot;:{&quot;1&quot;:0},&quot;4&quot;:{&quot;1&quot;:2,&quot;2&quot;:16776960},&quot;10&quot;:0,&quot;11&quot;:0,&quot;12&quot;:0,&quot;14&quot;:{&quot;1&quot;:2,&quot;2&quot;:0},&quot;15&quot;:&quot;Calibri, sans-serif&quot;,&quot;16&quot;:12}">O Programa Nacional Qualifica Mulher atender&aacute;, prioritariamente, mulheres que:<br /><br />I - possuam renda mensal de at&eacute; um sal&aacute;rio m&iacute;nimo e meio;<br /><br />II - tenham o ensino fundamental incompleto; e<br /><br />III - sejam v&iacute;timas de viol&ecirc;ncia dom&eacute;stica, em conson&acirc;ncia com o Plano Nacional de Enfrentamento ao Feminic&iacute;dio, nos termos do disposto no Decreto n&ordm; 10.906, de 20 de dezembro de 2021, e com outras pol&iacute;ticas de enfrentamento &agrave; viol&ecirc;ncia contra a mulher.</span></p>', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (788, 'Plano de Ação Nacional para a Conservação do Patrimônio Espeleológico Brasileiro - PAN Cavernas do Brasil', '2022-01-01', 'Não se aplica', 'Não se aplica', '646/2022', '2022-08-09', null, '<p><span data-sheets-value="{&quot;1&quot;:2,&quot;2&quot;:&quot;Art. 2&ordm; O PAN Cavernas do Brasil ter&aacute; como objetivo geral \&quot;Prevenir, reduzir e mitigar os impactos e danos antr&oacute;picos sobre o patrim&ocirc;nio espeleol&oacute;gico brasileiro, esp&eacute;cies e ambientes associados, em cinco anos.\&quot;\n\nPar&aacute;grafo &uacute;nico. Para atingir o objetivo previsto no caput ser&atilde;o estabelecidas a&ccedil;&otilde;es distribu&iacute;das em quatro objetivos espec&iacute;ficos, assim definidos:\n\npreven&ccedil;&atilde;o e redu&ccedil;&atilde;o dos impactos das atividades e empreendimentos efetiva ou potencialmente poluidores ou degradadores do patrim&ocirc;nio espeleol&oacute;gico brasileiro;\n\npreven&ccedil;&atilde;o e redu&ccedil;&atilde;o dos danos oriundos do uso desordenado e restaura&ccedil;&atilde;o do patrim&ocirc;nio espeleol&oacute;gico brasileiro e ambientes associados;\n\npromo&ccedil;&atilde;o da sustentabilidade nos usos tur&iacute;stico, desportivo, cient&iacute;fico, educativo e cultural do patrim&ocirc;nio espeleol&oacute;gico; e\n\namplia&ccedil;&atilde;o, divulga&ccedil;&atilde;o e dissemina&ccedil;&atilde;o do conhecimento t&eacute;cnico cient&iacute;fico e valoriza&ccedil;&atilde;o do patrim&ocirc;nio espeleol&oacute;gico brasileiro e ambientes associados.&quot;}" data-sheets-userformat="{&quot;2&quot;:15235,&quot;3&quot;:{&quot;1&quot;:0},&quot;4&quot;:{&quot;1&quot;:2,&quot;2&quot;:16776960},&quot;10&quot;:0,&q