INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (630, 'Programa Nacional de Triagem Neonatal', '2001-01-01', 'Não se aplica', 'Não se aplica', '822/2001', '2001-06-06', null, 'Art. 1º § 1º O Programa ora instituído deve ser executado de forma articulada pelo Ministério da Saúde e pelas Secretarias de Saúde dos estados, Distrito Federal e municípios e tem por objetivo o desenvolvimento de ações de triagem neonatal em fase pré-sintomática, acompanhamento e tratamento das doenças congênitas detectadas inseridas no Programa em todos os nascidos-vivos, promovendo o acesso, o incremento da qualidade e da capacidade instalada dos laboratórios especializados e serviços de atendimento, bem como organizar e regular o conjunto destas ações de saúde;', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2001/prt0822_06_06_2001.html', 'Ação 8585 - PO 0005 FAEC', 'Portaria', 5, 1, 31, null, '2023-03-08 11:50:45', 'Art. 1º § 1º O Programa ora instituído deve ser executado de forma articulada pelo Ministério da Saúde e pelas Secretarias de Saúde dos estados, Distrito Federal e municípios e tem por objetivo o desenvolvimento de ações de triagem neonatal em fase pré-sintomática, acompanhamento e tratamento das doenças congênitas detectadas inseridas no Programa em todos os nascidos-vivos, promovendo o acesso, o incremento da qualidade e da capacidade instalada dos laboratórios especializados e serviços de atendimento, bem como organizar e regular o conjunto destas ações de saúde; § 2º O Programa Nacional de Triagem Neonatal se ocupará da triagem com detecção dos casos suspeitos, confirmação diagnóstica, acompanhamento e tratamento dos casos identificados nas seguintes doenças congênitas, de acordo com a respectiva Fase de Implantação do Programa: a - Fenilcetonúria; b - Hipotireoidismo Congênito; c - Doenças Falciformes e outras Hemoglobinopatias; d - Fibrose Cística.', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (789, 'Plano de Ação Nacional para a Conservação dos Insetos Polinizadores Ameaçados de Extinção - PAN Insetos Polinizadores', '2022-01-01', 'Não se aplica', 'Não se aplica', '1145/2022', '2022-12-05', null, '<p><span data-sheets-value="{&quot;1&quot;:2,&quot;2&quot;:&quot;Art. 2&ordm; O PAN Insetos Polinizadores ter&aacute; como objetivo geral \&quot;Reduzir o risco de extin&ccedil;&atilde;o dos insetos polinizadores, com a&ccedil;&otilde;es integradas da sociedade civil e institui&ccedil;&otilde;es governamentais, promovendo a conserva&ccedil;&atilde;o de seus habitats e a manuten&ccedil;&atilde;o das fun&ccedil;&otilde;es e servi&ccedil;os ecossist&ecirc;micos de poliniza&ccedil;&atilde;o\&quot;.\n\nPar&aacute;grafo &uacute;nico. Para atingir o objetivo previsto no caput ser&atilde;o estabelecidas a&ccedil;&otilde;es distribu&iacute;das em oito objetivos espec&iacute;ficos, assim definidos:\n\nI - redu&ccedil;&atilde;o do uso e dos efeitos nocivos, diretos e indiretos, de agrot&oacute;xicos e de outras subst&acirc;ncias t&oacute;xicas sobre os insetos polinizadores e suas plantas associadas;\n\nII - promo&ccedil;&atilde;o da conectividade, melhoria da qualidade e restaura&ccedil;&atilde;o dos habitats, visando o aumento de popula&ccedil;&otilde;es de polinizadores e sua diversidade gen&eacute;tica;\n\nIII - redu&ccedil;&atilde;o da perda e degrada&ccedil;&atilde;o de habitat relacionadas &agrave; convers&atilde;o de &aacute;reas naturais para agricultura e silvicultura, especialmente monoculturas, pecu&aacute;ria e ind&uacute;stria carvoeira;\n\nIV - redu&ccedil;&atilde;o da perda e degrada&ccedil;&atilde;o de habitat relacionadas &agrave; convers&atilde;o de &aacute;reas naturais para atividades urbanas, industriais e empreendimentos;\n\nV - redu&ccedil;&atilde;o dos inc&ecirc;ndios, controle das queimadas antr&oacute;picas e manejo adequado do fogo, a fim de minimizar os impactos negativos sobre as esp&eacute;cies-alvo;\n\nVI - promo&ccedil;&atilde;o do manejo conservacionista de abelhas nativas e interrup&ccedil;&atilde;o da extra&ccedil;&atilde;o irregular, do transporte e do com&eacute;rcio ilegais de col&ocirc;nias ou suas partes;\n\nVII - redu&ccedil;&atilde;o das causas e mitiga&ccedil;&atilde;o dos efeitos negativos das mudan&ccedil;as clim&aacute;ticas sobre os insetos polinizadores; e\n\nVIII - manejo das esp&eacute;cies ex&oacute;ticas invasoras ou nocivas para as popula&ccedil;&otilde;es de insetos polinizadores nativos.&quot;}" data-sheets-userformat="{&quot;2&quot;:15235,&quot;3&quot;:{&quot;1&quot;:0},&quot;4&quot;:{&quot;1&quot;:2,&quot;2&quot;:16776960},&quot;10&quot;:0,&quot;11&quot;:0,&quot;12&quot;:0,&quot;14&quot;:{&quot;1&quot;:2,&quot;2&quot;:0},&quot;15&quot;:&quot;Calibri, sans-serif&quot;,&quot;16&quot;:12}">Art. 2&ordm; O PAN Insetos Polinizadores ter&aacute; como objetivo geral "Reduzir o risco de extin&ccedil;&atilde;o dos insetos polinizadores, com a&ccedil;&otilde;es integradas da sociedade civil e institui&ccedil;&otilde;es governamentais, promovendo a conserva&ccedil;&atilde;o de seus habitats e a manuten&ccedil;&atilde;o das fun&ccedil;&otilde;es e servi&ccedil;os ecossist&ecirc;micos de poliniza&ccedil;&atilde;o".<br /><br />Par&aacute;grafo &uacute;nico. Para atingir o objetivo previsto no caput ser&atilde;o estabelecidas a&ccedil;&otilde;es distribu&iacute;das em oito objetivos espec&iacute;ficos, assim definidos:<br /><br />I - redu&ccedil;&atilde;o do uso e dos efeitos nocivos, diretos e indiretos, de agrot&oacute;xicos e de outras subst&acirc;ncias t&oacute;xicas sobre os insetos polinizadores e suas plantas associadas;<br /><br />II - promo&ccedil;&atilde;o da conectividade, melhoria da qualidade e restaura&ccedil;&atilde;o dos habitats, visando o aumento de popula&ccedil;&otilde;es de polinizadores e sua diversidade gen&eacute;tica;<br /><br />III - redu&ccedil;&atilde;o da perda e degrada&ccedil;&atilde;o de habitat relacionadas &agrave; convers&atilde;o de &aacute;reas naturais para agricultura e silvicultura, especialmente monoculturas, pecu&aacute;ria e ind&uacute;stria carvoeira;<br /><br />IV - redu&ccedil;&atilde;o da perda e degrada&ccedil;&atilde;o de habitat relacionadas &agrave; convers&atilde;o de &aacute;reas naturais para atividades urbanas, industriais e empreendimentos;<br /><br />V - redu&ccedil;&atilde;o dos inc&ecirc;ndios, controle das queimadas antr&oacute;picas e manejo adequado do fogo, a fim de minimizar os impactos negativos sobre as esp&eacute;cies-alvo;<br /><br />VI - promo&ccedil;&atilde;o do manejo conservacionista de abelhas nativas e interrup&ccedil;&atilde;o da extra&ccedil;&atilde;o irregular, do transporte e do com&eacute;rcio ilegais de col&ocirc;nias ou suas partes;<br /><br />VII - redu&ccedil;&atilde;o das causas e mitiga&ccedil;&atilde;o dos efeitos negativos das mudan&ccedil;as clim&aacute;ticas sobre os insetos polinizadores; e<br /><br />VIII - manejo das esp&eacute;cies ex&oacute;ticas invasoras ou nocivas para as popula&ccedil;&otilde;es de insetos polinizadores nativos.</span></p>', null, 'Não se aplica', 'Portaria', 3, 5, 17, '2023-04-23 19:54:31', '2023-04-23 19:54:31', '<p><span data-sheets-value="{&quot;1&quot;:2,&quot;2&quot;:&quot;Art. 1&ordm; Fica aprovado o Plano de A&ccedil;&atilde;o Nacional para a Conserva&ccedil;&atilde;o Plano de A&ccedil;&atilde;o Nacional para a Conserva&ccedil;&atilde;o dos Insetos Polinizadores Amea&ccedil;ados de Extin&ccedil;&atilde;o - PAN Insetos Polinizadores, em conformidade com a Instru&ccedil;&atilde;o Normativa ICMBio n&ordm; 21, de 18 de dezembro de 2018.\n\n&sect; 1&ordm; O PAN Insetos Polinizadores abranger&aacute; e estabelecer&aacute; estrat&eacute;gias priorit&aacute;rias de conserva&ccedil;&atilde;o para 56 esp&eacute;cies amea&ccedil;adas de extin&ccedil;&atilde;o constantes da Lista Nacional de Esp&eacute;cies Amea&ccedil;adas de Extin&ccedil;&atilde;o, sendo 18 classificadas na categoria CR (Criticamente em Perigo) - Actinote zikani, Drephalys mourei, Eresia erysice erysice, Joiceya praeclarus, Mcclungia cymo fallens, Melinaea mnasias thera, Mimoides lysithous harrisianus, Napeogenes rhezia rhezia, Nyceryx mielkei, Parides bunichus chamissonia, Parides burchellanus, Parides klagesi, Parides panthonus castilhoi, Praepedaliodes sequeirae, Pseudocroniades machaon seabrai, Rhetus belphegor, Doxocopa zalmunna, Hyalyris fiammetta; 28 classificadas na categoria EN (Em Perigo) - Adelpha atlantica, Arawacus aethesa, Charonias theano, Drephalys miersi, Episcada vitrea, Glennia pylotis, Heliconius nattereri, Heraclides himeros baia, Heraclides himeros himeros, Magnastigma julia, Melipona capixaba, Melipona rufiventris, Melipona scutellaris, Pampasatyrus glaucope boenninghauseni, Pampasatyrus glaucope glaucope, Pampasatyrus gyrtone, Pampasatyrus reticulata gagarini, Parelbella polyzona, Parides ascanius, Parides tros danunciae, Partamona littoralis, Perrhybris pamela flava, Petrocerus catiena, Scada karschina delicata, Strymon ohausi, Turmada camposa, Voltinia sanarita, Zonia zonia diabo; e 10 classificadas na categoria VU (Vulner&aacute;vel) - Actinote quadra, Arhysosage cactorum, Cunizza hirlanda fulvinota, Cunizza hirlanda planasia, Mimoides lysithous sebastianus, Moschoneura pinthous methymna, Olafia roscius iphimedia, Pampasatyrus glaucope eberti, Panara ovifera, Tithorea harmonia caissara.\n\n&sect; 2&ordm; O PAN estabelecer&aacute;, de maneira concomitante, estrat&eacute;gias para conserva&ccedil;&atilde;o para outras cinco esp&eacute;cies classificadas como Quase Amea&ccedil;ada (NT) - Euglossa botocuda, Euglossa marianae, Exaerete salsai, Plebeia grapiuna, Scaura cearensis; e outras 13 esp&eacute;cies validadas como amea&ccedil;adas, segundo o resultado da avalia&ccedil;&atilde;o nacional do estado de conserva&ccedil;&atilde;o, realizada pelo ICMBio: Actenosigynes mantiqueirensis, Albinapis gracilis, Arhysosage flava, Bombus bahiensis, Bombus bellicosus, Centris hyptidoides, Centris thelyopsis, Eufriesea brasilianorum, Eufriesea pyrrhopyga, Euglossa avicula, Euglossa cyanochlora, Eulaema felipei, Schwarziana bocainensis.\n\n&sect; 3&ordm; Estabelecer&aacute; estrat&eacute;gias para conserva&ccedil;&atilde;o de outros dois t&aacute;xons categorizados no estado da Bahia (Portaria SEMA N&ordm; 37, de 15 de agosto de 2017) como: EN (Em Perigo) - Melipona subnitida; VU (Vulner&aacute;vel) - Melipona mandacaia.\n\n&sect; 4&ordm; Estabelecer&aacute; estrat&eacute;gias para conserva&ccedil;&atilde;o de outros 11 t&aacute;xons categorizados no estado de Minas Gerais (Delibera&ccedil;&atilde;o Normativa COPAM n&ordm; 147, de 30 de abril de 2010) como: EN (Em Perigo) - Epicharis minima, Eufriesea aeneiventris, Euglossa decorata, Hexantheda missionica; VU (Vulner&aacute;vel) - Cyanophrys bertha, Epicharis pygialis, Hypoleria fallens, Melipona asilvai, Paratrigona incerta, Partamona ailyae, Partamona combinata.\n\n&sect; 5&ordm; Estabelecer&aacute; estrat&eacute;gias para conserva&ccedil;&atilde;o de outros seis t&aacute;xons categorizados no estado do Par&aacute; (Resolu&ccedil;&atilde;o COEMA n&ordm; 54, de 24 de outubro de 2007) como: EN (Em Perigo) - Heraclides chiansiades, Heraclides garleppi lecerfi, Hypoleria lavinia mulviana, Parides hahneli, Parides panthonus; VU (Vulner&aacute;vel) - Pterourus xanthopleura.\n\n&sect; 6&ordm; Estabelecer&aacute; estrat&eacute;gias para conserva&ccedil;&atilde;o de outros 14 t&aacute;xons categorizados no estado de S&atilde;o Paulo (Decreto Estadual N&ordm; 63.853, de 27 de novembro de 2018) como: CR (Criticamente em Perigo) - Cephalotrigona capitata, Exomalopsis atlantica, Melipona fuliginosa; EN (Em Perigo) - Pampasatyrus reticulata, Scada karschina karschina; VU (Vulner&aacute;vel) - Arhysoceble picta, Lanthanomelissa betinae, Lanthanomelissa clementis, Melipona bicolor, Melipona marginata, Melipona torrida, Melipona quinquefasciata, Melipona mondury, Tetraglossula fucosa.&quot;}" data-sheets-userformat="{&quot;2&quot;:15235,&quot;3&quot;:{&quot;1&quot;:0},&quot;4&quot;:{&quot;1&quot;:2,&quot;2&quot;:16776960},&quot;10&quot;:0,&quot;11&quot;:0,&quot;12&quot;:0,&quot;14&quot;:{&quot;1&quot;:2,&quot;2&quot;:0},&quot;15&quot;:&quot;Calibri, sans-serif&quot;,&quot;16&quot;:12}">Art. 1&ordm; Fica aprovado o Plano de A&ccedil;&atilde;o Nacional para a Conserva&ccedil;&atilde;o Plano de A&ccedil;&atilde;o Nacional para a Conserva&ccedil;&atilde;o dos Insetos Polinizadores Amea&ccedil;ados de Extin&ccedil;&atilde;o - PAN Insetos Polinizadores, em conformidade com a Instru&ccedil;&atilde;o Normativa ICMBio n&ordm; 21, de 18 de dezembro de 2018.<br /><br />&sect; 1&ordm; O PAN Insetos Polinizadores abranger&aacute; e estabelecer&aacute; estrat&eacute;gias priorit&aacute;rias de conserva&ccedil;&atilde;o para 56 esp&eacute;cies amea&ccedil;adas de extin&ccedil;&atilde;o constantes da Lista Nacional de Esp&eacute;cies Amea&ccedil;adas de Extin&ccedil;&atilde;o, sendo 18 classificadas na categoria CR (Criticamente em Perigo) - Actinote zikani, Drephalys mourei, Eresia erysice erysice, Joiceya praeclarus, Mcclungia cymo fallens, Melinaea mnasias thera, Mimoides lysithous harrisianus, Napeogenes rhezia rhezia, Nyceryx mielkei, Parides bunichus chamissonia, Parides burchellanus, Parides klagesi, Parides panthonus castilhoi, Praepedaliodes sequeirae, Pseudocroniades machaon seabrai, Rhetus belphegor, Doxocopa zalmunna, Hyalyris fiammetta; 28 classificadas na categoria EN (Em Perigo) - Adelpha atlantica, Arawacus aethesa, Charonias theano, Drephalys miersi, Episcada vitrea, Glennia pylotis, Heliconius nattereri, Heraclides himeros baia, Heraclides himeros himeros, Magnastigma julia, Melipona capixaba, Meli