INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (513, 'Política Nacional de Atenção ao Portador de Doença Neurológica', '2005-01-01', 'Não se aplica', 'Não se aplica', '1161/2005', '2005-07-07', null, 'Art. 2°  Estabelecer que a Política Nacional de Atenção ao Portador de Doença Neurológica seja organizada de forma articulada entre o Ministério da Saúde, as Secretarias de Estado da Saúde e as Secretarias Municipais de Saúde, permitindo: I - desenvolver estratégias de promoção da qualidade de vida, educação, proteção e recuperação da saúde e prevenção de danos, protegendo e desenvolvendo a autonomia e a eqüidade de indivíduos e coletividades; II - organizar uma linha de cuidados integrais (promoção, prevenção, tratamento e recuperação) que perpasse todos os níveis de atenção, promovendo, dessa forma, a inversão do modelo de atenção; III - identificar os determinantes e condicionantes das principais patologias que levam às doenças neurológicas e ao desenvolvimento de ações transetoriais de responsabilidade pública, sem excluir as responsabilidades de toda a sociedade; IV - definir critérios técnicos mínimos para o funcionamento e a avaliação dos serviços públicos e privados que realizam tratamento clínico, intervencionista e/ou cirúrgico, bem como os mecanismos de sua monitoração com vistas a diminuir os riscos aos quais fica exposto o portador de doença neurológica; V - ampliar e qualificar a cobertura do atendimento aos portadores de doenças neurológicas no Brasil, garantindo a universalidade, a eqüidade, a integralidade, o controle social e o acesso às diferentes modalidades terapêuticas; VI - ampliar e qualificar a cobertura dos portadores de hipertensão arterial e de diabetes mellitus, principais causas dos acidentes vasculares cerebrais no Brasil; VII - fomentar, coordenar e executar projetos estratégicos que visem o estudo do custo-efetividade, da eficácia e da qualidade, bem como a incorporação tecnológica do processo de diagnose e do tratamento clínico, intervencionista e/ou cirúrgico no Brasil; VIII - contribuir para o desenvolvimento de processos e métodos de coleta, análise e organização dos resultados das ações decorrentes da Política Nacional de Atenção ao Portador de Doença Neurológica, permitindo que a partir de seu desempenho seja possível um aprimoramento da gestão, disseminação das informações e uma visão dinâmica do estado de saúde das pessoas com doença neurológica; IX - promover intercâmbio com outros subsistemas de informações setoriais, implementando e aperfeiçoando permanentemente a produção de dados e garantindo a democratização das informações; e X - qualificar a assistência e promover a educação permanente dos profissionais de saúde envolvidos com a implantação e a implementação da Política Nacional de Atenção ao Portador de Doença Neurológica, em acordo com os princípios da integralidade e da humanização.', '#N/A', 'Política sem ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'Sem especificação  População em geral', null);
