INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (174, 'Programa Nacional de Descentralização', '1994-01-01', 'Não se aplica', 'Não se aplica', '1044/1994', '1994-01-17', null, 'Art. 1° Fica instituído o Programa Nacional de Descentralização com o objetivo de fortalecer a Federação e proporcionar melhores condições para o desenvolvimento nacional, a ser implantado e progressivamente executado mediante a repartição e descentralização das atribuições da União, a fim de tornar a rede de serviços públicos mais acessível à população e de estabelecer condições para a melhor aplicação dos recursos públicos, eliminando a dualidade e a superposição de ações dos órgãos e entidades da Administração Pública Federal.', '#N/A', 'Programa sem ação orçamentária associada', 'Decreto', 5, 6, 8, null, null, 'Art. 3° O Programa Nacional de Descentralização será iniciado com o estudo e a implementação das providências relativas à extinção do Ministério do Bem-Estar Social e do Ministério da Integração Regional.<br />Art. 4° Sem prejuízo do disposto no art. 3°, o Programa compreenderá inicial e preferencialmente as seguintes áreas de atuação governamental:<br />I - assistência social; II - saúde; III - defesa sanitária; IV - previdência social;<br />V - educação; VI - irrigação; VII - recursos hídricos; VIII - habitação e saneamento básico; IX - transportes; X - meio ambiente; XI - eletrificação rural; XII - telefonia rural; XIII - abastecimento.', null);
