INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (70, 'Programa Auxílio-Gás', '2002-01-01', 'Não se aplica', 'Não se aplica', '4102/2002', '2002-01-25', null, 'Art. 1o Fica instituído o programa "Auxílio-Gás", destinado a subsidiar o preço do gás liqüefeito de petróleo às famílias de baixa renda.', '#N/A', 'Programa sem ação orçamentária associada', 'Decreto', 5, 1, 6, null, null, 'Art. 3o Para os efeitos do disposto neste Decreto, é considerada de baixa renda a família que atenda, cumulativamente, aos seguintes requisitos:<br />I - possuir renda mensal per capita máxima equivalente a meio salário mínimo definido pelo Governo Federal; e<br />II - atender a pelo menos uma das seguintes condições cadastrais:<br />a) ser integrante do Cadastramento Único para Programas Sociais do Governo Federal, criado pelo Decreto no 3.877, de 24 de julho de 2001; ou<br />b) ser beneficiária do programas "Bolsa Escola" ou "Bolsa Alimentação", ou estar cadastrada como potencial beneficiária desses programas.<br />Parágrafo único. Do cálculo da renda familiar mensal serão excluídos os rendimentos provenientes das seguintes origens: I - Bolsa Escola; II - Bolsa Alimentação; III - Erradicação do Trabalho Infantil; IV - Seguro Desemprego; V - Seguro Safra; e VI - Bolsa Qualificação.', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (199, 'Programa Nacional de Universalização do Acesso e Uso da Energia Elétrica na Amazônia Legal (Mais Luz para a Amazônia)', '2020-01-01', 'Não se aplica', 'Não se aplica', '10221/2020', '2020-02-06', null, 'Art. 1º  Fica instituído o Programa Nacional de Universalização do Acesso e Uso da Energia Elétrica na Amazônia Legal - Mais Luz para a Amazônia, com a finalidade de fornecer o atendimento com energia elétrica à população brasileira residente em regiões remotas da Amazônia Legal.', '#N/A', 'Programa sem ação orçamentária associada', 'Decreto', 5, 1, 13, null, null, '§ 2º  São prioridades para o atendimento:<br /><br />I - as famílias de baixa renda inscritas no Cadastro Único para Programas Sociais do Governo Federal;<br /><br />II - as famílias beneficiárias de programas de governo federal, estadual ou municipal que tenham por objeto o desenvolvimento social e econômico;<br /><br />III - os assentamentos rurais, as comunidades indígenas, os territórios quilombolas e as demais comunidades localizadas em reservas extrativistas ou impactadas diretamente por empreendimentos de geração ou de transmissão de energia elétrica cuja responsabilidade não seja do próprio concessionário;<br /><br />IV - as escolas, os postos de saúde e os poços de água comunitários; e<br /><br />V - as famílias residentes em unidades de conservação.', null);
