INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (420, 'Programa Nacional de Microcrédito Produtivo Orientado (PNMPO)', '2005-01-01', '226/2004', '30 de novembro de 2004', '11110/2005', '2005-04-26', '2018-03-20', 'Art. 1º Fica instituído, no âmbito do Ministério do Trabalho e Emprego, o Programa Nacional de Microcrédito Produtivo Orientado - PNMPO, com o objetivo de incentivar a geração de trabalho e renda entre os microempreendedores populares. ', 'A lei que revoga institui o programa', 'Programa sem ação orçamentária associada', 'Lei ordinária', 5, 3, 12, null, null, '§ 1º São beneficiárias do PNMPO as pessoas físicas e jurídicas empreendedoras de atividades produtivas de pequeno porte, a serem definidas em regulamento, especificamente para fins do PNMPO. (Revogado pela Lei nº 13.636, de 2018) § 1º São beneficiárias do PNMPO pessoas naturais e jurídicas empreendedoras de atividades produtivas urbanas e rurais, apresentadas de forma individual ou coletiva. (Redação dada pela  Lei nº 13.636, de 2018)', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (249, 'Auxílio Emergencial de abril a junho de 2020', '2020-01-01', 'Não se aplica', 'Não se aplica', '13982/2020', '2020-04-02', null, 'Art. 2º Durante o período de 3 (três) meses, a contar da publicação desta Lei, será concedido auxílio emergencial no valor de R$ 600,00 (seiscentos reais) mensais ao trabalhador que cumpra cumulativamente os seguintes requisitos', 'Regulamentado pelo Decreto 10.316 de 07/04/2020, prorrogado e alterado pelo Decreto 10412/202, MP 1000/2020, MP 1039/2021 e Decreto 10740/21.', '#N/A', 'Lei ordinária', 5, 1, 6, null, null, 'Art. 2º...     ...trabalhador que cumpra cumulativamente os seguintes requisitos:                                              I - seja maior de 18 (dezoito) anos de idade, salvo no caso de mães adolescentes; (Redação dada pela Lei nº 13.998, de 2020)<br /><br />II - não tenha emprego formal ativo;<br /><br />III - não seja titular de benefício previdenciário ou assistencial ou beneficiário do seguro-desemprego ou de programa de transferência de renda federal, ressalvado, nos termos dos §§ 1º e 2º, o Bolsa Família;<br /><br />IV - cuja renda familiar mensal per capita seja de até 1/2 (meio) salário-mínimo ou a renda familiar mensal total seja de até 3 (três) salários mínimos;<br /><br />V - que, no ano de 2018, não tenha recebido rendimentos tributáveis acima de R$ 28.559,70 (vinte e oito mil, quinhentos e cinquenta e nove reais e setenta centavos; e<br /><br />VI - que exerça atividade na condição de:<br /><br />a) microempreendedor individual (MEI;<br /><br />b) contribuinte individual do Regime Geral de Previdência Social que contribua na forma do caput ou do inciso I do § 2º do art. 21 da Lei nº 8.212, de 24 de julho de 1991 ; ou<br /><br />c) trabalhador informal, seja empregado, autônomo ou desempregado, de qualquer natureza, inclusive o intermitente inativo, inscrito no Cadastro Único para Programas Sociais do Governo Federal (CadÚnico) até 20 de março de 2020, ou que, nos termos de autodeclaração, cumpra o requisito do inciso IV.', null);
