INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (270, 'Política Energética Nacional', '1997-01-01', 'Não se aplica', 'Não se aplica', '9478/1997', '1997-08-07', null, 'Art. 1º As políticas nacionais para o aproveitamento racional das fontes de energia visarão aos seguintes objetivos: I - preservar o interesse nacional; II - promover o desenvolvimento, ampliar o mercado de trabalho e valorizar os recursos energéticos; III - proteger os interesses do consumidor quanto a preço, qualidade e oferta dos produtos; IV - proteger o meio ambiente e promover a conservação de energia; V - garantir o fornecimento de derivados de petróleo em todo o território nacional, nos termos do § 2º do art. 177 da Constituição Federal; VI - incrementar, em bases econômicas, a utilização do gás natural; VII - identificar as soluções mais adequadas para o suprimento de energia elétrica nas diversas regiões do País; VIII - utilizar fontes alternativas de energia, mediante o aproveitamento econômico dos insumos disponíveis e das tecnologias aplicáveis; IX - promover a livre concorrência; X - atrair investimentos na produção de energia; XI - ampliar a competitividade do País no mercado internacional. XII - incrementar, em bases econômicas, sociais e ambientais, a participação dos biocombustíveis na matriz energética nacional. (Redação dada pela Lei nº 11.097, de 2005) XIII - garantir o fornecimento de biocombustíveis em todo o território nacional.  (Incluído pela Medida Provisória nº 532, de 2011) XIII - garantir o fornecimento de biocombustíveis em todo o território nacional;             (Incluído pela Lei nº 12.490, de 2011) XIV - incentivar a geração de energia elétrica a partir da biomassa e de subprodutos da produção de biocombustíveis, em razão do seu caráter limpo, renovável e complementar à fonte hidráulica; (Incluído pela Lei nº 12.490, de 2011) XV - promover a competitividade do País no mercado internacional de biocombustíveis; (Incluído pela Lei nº 12.490, de 2011) XVI - atrair investimentos em infraestrutura para transporte e estocagem de biocombustíveis; (Incluído pela Lei nº 12.490, de 2011) XVII - fomentar a pesquisa e o desenvolvimento relacionados à energia renovável; (Incluído pela Lei nº 12.490, de 2011) XVIII - mitigar as emissões de gases causadores de efeito estufa e de poluentes nos setores de energia e de transportes, inclusive com o uso de biocombustíveis.(Incluído pela Lei nº 12.490, de 2011)', 'Alterações no objetivo', 'Política com ação orçamentária associada ', 'Lei ordinária', 4, 4, 13, null, null, 'Atividades relacionadas ao aproveitamento racional das fontes de energia', null);
