INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (477, 'Programa de Promoção da Sustentabilidade dos Espaços Sub-regionais (PROMESO)', '2009-01-01', 'Não se aplica', 'Não se aplica', 'Não se aplica', null, null, '#N/A', 'O Programa de Promoção da Sustentabilidade de Espaços Sub-Regionais (PROMESO) orienta-se pelas diretrizes da Política Nacional de Desenvolvimento Regional (PNDR), que busca a redução das desigualdades regionais e atua a partir de dois eixos principais: a organização social e a ativação econômica. O primeiro eixo volta-se ao estímulo e fortalecimento dos fóruns mesorregionais para que sejam canais de participação da sociedade civil na definição de ações a serem contempladas com investimentos públicos. O eixo da ativação econômica se dá por meio do fomento aos Arranjos Produtivos Locais identificados como prioritários nas Mesorregiões Diferenciadas e nas Regiões Integradas de Desenvolvimento-RIDEs (BRASIL, 2009, p. 10).', 'Não se aplica', 'Não se aplica', 5, 3, 5, null, null, 'Sem especificação', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (479, 'Programa Gestão de Riscos e Desastres', '2012-01-01', 'Não se aplica', 'Não se aplica', 'Não se aplica', null, null, '0172 - Aprimorar a coordenação e a gestão das ações de preparação, prevenção, mitigação, resposta e recuperação para a proteção e defesa civil por meio do fortalecimento do Sistema Nacional de Proteção e Defesa Civil – SINPDEC, inclusive pela articulação federativa e internacional.', 'Está inserido no marco legal da Política Nacional de Proteção e Defesa Civil', 'NA', 'Não se aplica', 5, 4, 13, null, null, 'Sem especificação', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (271, 'Política Nacional da Agricultura Familiar  e Empreendimentos Familiares Rurais', '2006-01-01', 'Não se aplica', 'Não se aplica', '11326/2006', '2006-07-24', null, 'Sem especificação', 'Alterações no público-alvo', 'Política com ação orçamentária associada', 'Lei ordinária', 4, 3, 4, null, null, 'Art. 3º Para os efeitos desta Lei, considera-se agricultor familiar e empreendedor familiar rural aquele que pratica atividades no meio rural, atendendo, simultaneamente, aos seguintes requisitos:<br />I - não detenha, a qualquer título, área maior do que 4 (quatro) módulos fiscais;<br />II - utilize predominantemente mão-de-obra da própria família nas atividades econômicas do seu estabelecimento ou empreendimento;<br />III - tenha renda familiar predominantemente originada de atividades econômicas vinculadas ao próprio estabelecimento ou empreendimento;<br />III - tenha percentual mínimo da renda familiar originada de atividades econômicas do seu estabelecimento ou empreendimento, na forma definida pelo Poder Executivo; (Redação dada pela Lei nº 12.512, de 2011)<br />IV - dirija seu estabelecimento ou empreendimento com sua família.<br />§ 1º O disposto no inciso I do caput deste artigo não se aplica quando se tratar de condomínio rural ou outras formas coletivas de propriedade, desde que a fração ideal por proprietário não ultrapasse 4 (quatro) módulos fiscais.<br />§ 2º São também beneficiários desta Lei:<br />I - silvicultores que atendam simultaneamente a todos os requisitos de que trata o caput deste artigo, cultivem florestas nativas ou exóticas e que promovam o manejo sustentável daqueles ambientes;<br />II - aqüicultores que atendam simultaneamente a todos os requisitos de que trata o caput deste artigo e explorem reservatórios hídricos com superfície total de até 2ha (dois hectares) ou ocupem até 500m³ (quinhentos metros cúbicos) de água, quando a exploração se efetivar em tanques-rede;<br />III - extrativistas que atendam simultaneamente aos requisitos previstos nos incisos II, III e IV do caput deste artigo e exerçam essa atividade artesanalmente no meio rural, excluídos os garimpeiros e faiscadores;<br />IV - pescadores que atendam simultaneamente aos requisitos previstos nos incisos I, II, III e IV do caput deste artigo e exerçam a atividade pesqueira artesanalmente.<br />V - povos indígenas que atendam simultaneamente aos requisitos previstos nos incisos II, III e IV do caput do art. 3º ; (Incluído pela Lei nº 12.512, de 2011)<br />VI - integrantes de comunidades remanescentes de quilombos rurais e demais povos e comunidades tradicionais que atendam simultaneamente aos incisos II, III e IV do caput do art. 3º . (Incluído pela Lei nº 12.512, de 2011)', null);
