INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (521, 'Política Nacional de Atenção Integral à Saúde da Criança', '2015-01-01', 'Não se aplica', 'Não se aplica', '1130/2015', '2015-08-05', null, 'Art. 2º A PNAISC tem por objetivo promover e proteger a saúde da criança e o aleitamento materno, mediante a atenção e cuidados integrais e integrados da gestação aos 9 (nove) anos de vida, com especial atenção à primeira infância e às populações de maior vulnerabilidade, visando à redução da morbimortalidade e um ambiente facilitador à vida com condições dignas de existência e pleno desenvolvimento.', '#N/A', 'Política sem ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'Art. 3º Para fins da PNAISC, considera-se:<br />I - criança: pessoa na faixa etária de 0 (zero) a 9 (nove) anos, ou seja, de 0 (zero) a 120 (cento e vinte) meses; e II - primeira infância: pessoa na faixa etária de 0 (zero) a 5 (cinco) anos, ou seja, de 0 (zero) a 72 (setenta e dois) meses.Paragrafo único. Para fins de atendimento em serviços de pediatria no SUS, a PNAISC contemplará crianças e adolescentes até a idade de 15 (quinze) anos, ou seja, 192 (cento e noventa e dois) meses, sendo este limite etário passível de alteração de acordo com as normas e rotinas do estabelecimento de saúde responsável pelo atendimento.', null);
