INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (304, 'Política Nacional de Sangue, Componentes e Hemoderivados', '2001-01-01', 'Não se aplica', 'Não se aplica', '10205/2001', '2001-03-22', null, 'Art. 8o A Política Nacional de Sangue, Componentes e Hemoderivados terá por finalidade garantir a auto-suficiência do País nesse setor e harmonizar as ações do poder público em todos os níveis de governo, e será implementada, no âmbito do Sistema Único de Saúde, pelo Sistema Nacional de Sangue, Componentes e Derivados - SINASAN', '#N/A', 'Política sem ação orçamentária associada', 'Lei ordinária', 4, 1, 1, null, null, 'XVI - populações do campo e da floresta: povos e comunidades que têm seus modos de vida, produção e reprodução social relacionados predominantemente com o campo, a floresta, os ambientes aquáticos, a agropecuária e o extrativismo, como: camponeses; agricultores familiares; trabalhadores rurais assalariados e temporários que residam ou não no campo; trabalhadores rurais assentados e acampados; comunidades de quilombos; populações que habitam ou usam reservas extrativistas; populações ribeirinhas; populações atingidas por barragens; outras comunidades tradicionais; dentre outros;<br /><br />XVII - povos e comunidades tradicionais: grupos culturalmente diferenciados que se reconhecem como tradicionais, possuem formas próprias de organização social e ocupam e utilizam territórios e recursos naturais como condição para sua produção e reprodução cultural, social, religiosa, ancestral e econômica, utilizando conhecimentos e inovações práticas gerados e transmitidos pela tradição;', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (550, 'Política Nacional de Saúde do Trabalhador e da Trabalhadora', '2012-01-01', 'Não se aplica', 'Não se aplica', '1823/2012', '2012-08-23', null, 'Art. 2º A Política Nacional de Saúde do Trabalhador e da Trabalhadora tem como finalidade definir os princípios, as diretrizes e as estratégias a serem observados pelas três esferas de gestão do Sistema Único de Saúde (SUS), para o desenvolvimento da atenção integral à saúde do trabalhador, com ênfase na vigilância, visando a promoção e a proteção da saúde dos trabalhadores e a redução da morbimortalidade decorrente dos modelos de desenvolvimento e dos processos produtivos. (Origem: PRT MS/GM 1823/2012, Art. 2º)', '#N/A', 'Política sem ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'Sem especificação  População em geral', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (324, 'Programa Cartão Reforma', '2017-01-01', '751/2016', '10 de novembro de 2016', '13439/2017', '2017-04-28', '2017-04-28', 'Art. 1º Fica instituído o Programa Cartão Reforma, que tem por finalidade a concessão de subvenção econômica para aquisição de materiais de construção, destinada à reforma, à ampliação ou à conclusão de unidades habitacionais dos grupos familiares contemplados, incluídos o fornecimento de assistência técnica e os custos operacionais do Programa que estejam a cargo da União.', 'Revogada pela Lei nº 14.118, de 2021', 'Programa sem ação orçamentária associada', 'Lei ordinária', 5, 1, 11, null, null, 'Art. 7º Para participar do Programa, o candidato a beneficiário deverá atender, no mínimo, aos seguintes requisitos:<br />I - integrar grupo familiar com renda mensal de até R$ 2.811,00 (dois mil, oitocentos e onze reais;<br />II - ser proprietário, possuidor ou detentor de imóvel residencial, em áreas regularizadas ou passíveis de regularização, na forma da lei, excluído o ocupante de imóveis cedidos ou alugados; e<br />III - ser maior de dezoito anos ou emancipado.', null);
