INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (347, 'Programa de Desligamento Voluntário de Servidores civis', '1997-01-01', '1530-7/1997', '13 de junho de 1997', '9468/1997', '1997-07-11', null, 'Art. 1º Fica Instituído, no âmbito do Poder Executivo Federal, o Programa de Desligamento Voluntário - PDV, do servidor público civil, com o objetivo de possibilitar melhor alocacão dos recursos humanos, propiciar a modernização da Administração e auxiliar no equilíbrio das contas públicas.', '#N/A', 'Programa sem ação orçamentária associada', 'Lei ordinária', 5, 6, 8, null, null, 'Art. 2º Poderão aderir ao PDV os servidores públicos civis da Administração direta, autárquica e fundacional, inclusive dos extintos territórios, ocupantes de cargo efetivo, exceto os ocupantes dos cargos relacionados no Anexo e aqueles que:<br />I - estejam em estágio probatório;<br />II - tenham requerido aposentadoria;<br />III - tenham se aposentado em função pública, em cargo cuja acumulação não esteja prevista no art. 37, XVI e XVII, da Constituição, e tenham optado pela remuneração do cargo efetivo que ocupem;<br />IV - tenham sido condenados por decisão judicial transitada em julgado, que importe na perda do cargo;<br />V - estejam afastados nas condições previstas nos incisos I e II do art. 229 da Lei nº 8.112, de 11 de dezembro de 1990; <br />VI - estejam afastados em virtude de licença para tratamento de saúde, quando acometidos das doenças especificadas no § 1º do art. 186 da Lei nº 8.112, de 1990.', null);
