INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (620, 'Programa Nacional de Melhoria do Acesso e da Qualidade da Atenção Básica (PMAQ-AB)', '2015-01-01', 'Não se aplica', 'Não se aplica', '1645/2015', '2015-10-02', '2019-11-12', 'Art. 2º O PMAQ-AB tem como objetivo induzir a ampliação do acesso e a melhoria da qualidade da atenção básica, com garantia de um padrão de qualidade comparável nacional, regional e localmente, de maneira a permitir maior transparência e efetividade das ações governamentais direcionadas à Atenção Básica em Saúde.', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2015/prt1645_01_10_2015.html', 'Ação 20AD, PO 0008 (2013 a 2017) e 219A, parte do PO 0001 (2018 em diante - valores do PMAQ ainda são transferidos durante a transição do Previne Brasil)', 'Portaria', 5, 1, 1, null, null, 'Art. 5º A Fase 1 do PMAQ-AB é denominada Adesão e Contratualização. § 1º Na Fase 1, todas as equipes de saúde da atenção básica, incluindo as equipes de saúde bucal e Núcleos de Apoio ao Saúde da Família, independente do modelo pelo qual se organizam, poderão aderir ao PMAQ-AB, desde que se encontrem em conformidade com os princípios da atenção básica e com os critérios a serem definidos no Manual Instrutivo do PMAQ-AB. ', null);
