INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (637, 'Programa para o Desenvolvimento do Complexo Industrial da Saúde (PROCIS)', '2012-01-01', 'Não se aplica', 'Não se aplica', '506/2012', '2012-03-21', null, 'Art. 1º Fica criado o Programa para o Desenvolvimento do Complexo Industrial da Saúde (PROCIS), com o objetivo de fortalecer os produtores públicos e a infraestrutura de produção e inovação em saúde do setor público. Parágrafo único. Para efeitos desta Portaria são considerados produtores públicos os órgãos ou entidades que integrem a administração pública e sejam responsáveis pela produção de fármacos, biofármacos, medicamentos, imunobiológicos, produtos médicos, equipamentos e materiais de uso em saúde e "kits" para diagnóstico de uso "in vitro", primordialmente destinados aos programas estratégicos de saúde pública. Art. 2º O PROCIS tem os seguintes objetivos: I - apoiar a modernização e estruturação produtiva e gerencial; II - apoiar a qualificação da produção e manter vigente o Certificado de Boas Práticas de Fabricação (CBPF) emitido pela Agência Nacional de Vigilância Sanitária (ANVISA); III - fortalecer as parcerias para o desenvolvimento produtivo visando desenvolver e absorver produtos estratégicos para o Sistema Único de Saúde; IV - apoiar a qualificação da gestão com vistas a promover maior eficiência e efetividade; V - apoiar o desenvolvimento tecnológico e a transferência de tecnologias, estratégicos para o Sistema Único de Saúde (SUS); VI - manter o aproveitamento das complementariedades entre os laboratórios e respeitar as vocações e o perfil produtivo; e VII - apoiar a infraestrutura pública de tecnologia e inovação para suporte à produção no país de produtos estratégicos para o SUS. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2012/prt0506_21_03_2012.html', 'Programa com ação orçamentária associada', 'Portaria', 5, 1, 1, null, null, 'Art. 1º Fica criado o Programa para o Desenvolvimento do Complexo Industrial da Saúde (PROCIS), com o objetivo de fortalecer os produtores públicos e a infraestrutura de produção e inovação em saúde do setor público. Parágrafo único. Para efeitos desta Portaria são considerados produtores públicos os órgãos ou entidades que integrem a administração pública e sejam responsáveis pela produção de fármacos, biofármacos, medicamentos, imunobiológicos, produtos médicos, equipamentos e materiais de uso em saúde e "kits" para diagnóstico de uso "in vitro", primordialmente destinados aos programas estratégicos de saúde pública. ', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (643, 'Programa SOS Emergências', '2012-01-01', 'Não se aplica', 'Não se aplica', '1663/2012', '2012-08-06', null, 'Art. 2º São objetivos do Programa SOS Emergências: I - estimular a organização e a implantação da RUE, buscando um pacto cooperativo entre as instâncias de gestão do SUS para garantir os investimentos e recursos necessários a esta mudança de modelo de atenção nos grandes prontos-socorros dos estabelecimentos hospitalares integrados ao SUS; II - intervir de forma mais organizada, ágil e efetiva sobre a oferta do cuidado nas grandes emergências hospitalares do SUS; III - assessorar tecnicamente a equipe do hospital participante do Programa e oferecer capacitação para aprimoramento da gestão do cuidado ao paciente em situação de urgência e emergência; IV - apoiar as unidades hospitalares para a melhoria da gestão e da qualidade assistencial, por meio da implantação de dispositivos como a classificação de risco, gestão de leitos, implantação de protocolos clínico-assistenciais e administrativos, adequação da estrutura e ambiência hospitalar, regulação e articulação com o sistema de saúde; e V - possibilitar a readequação física e tecnológica dos serviços de urgência e emergência da unidade hospitalar.', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2012/prt1663_06_08_2012.html', 'Ações: 8585; 8535 e 8933', 'Portaria', 5, 1, 1, null, null, 'Art. 3º O Programa SOS Emergências será integrado por Portas de Entradas Hospitalares de Urgência selecionadas pelo Ministério da Saúde, em conjunto com as Secretarias de Saúde estaduais, distrital e municipais. Parágrafo único. Serão consideradas Portas de Entradas Hospitalares de Urgência prioritárias para o Programa SOS Emergências aquelas que cumprirem os seguintes requisitos: I - ser referência regional, realizando no mínimo dez por cento dos atendimentos aos usuários do SUS em situação de urgência e emergência oriundos de outros Municípios, conforme registro no Sistema de Informação Hospitalar (SIH/SUS; II - ter, no mínimo, cem leitos cadastrados no Sistema de Cadastro Nacional de Estabelecimentos (SCNES; III - estar habilitada em pelo menos uma das seguintes linhas de cuidado: a) cardiovascular; b) neurologia/neurocirurgia; c) traumato-ortopedia; ou d) ser referência em pediatria; IV - ter maior volume de produção de procedimentos ambulatoriais e de internação, conforme registro no SIH/SUS e no Sistema de Informação Ambulatorial (SIA/SUS), ou outro(s) que vierem a substituí-lo(s; e V - ter a aprovação do(s) gestor(es) estadual(is), municipal(is), distrital de saúde e do Ministério da Saúde, de acordo com o território abrangido pelo referenciamento dos usuários do SUS em situação de urgência e emergência para ingresso do estabelecimento hospitalar no Programa. ', null);
