INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (401, 'Programa Nacional de Acesso à Alimentação (PNAA-Fome Zero)', '2003-01-01', '108/2003', '28 de fevereiro de 2003', '10689/2003', '2003-06-16', '2003-05-23', 'Art. 1o Fica criado o Programa Nacional de Acesso à Alimentação – PNAA, vinculado às ações dirigidas ao combate à fome e à promoção da segurança alimentar e nutricional.', '#N/A', 'Programa sem ação orçamentária associada', 'Lei ordinária', 5, 1, 6, null, null, 'Art. 2º O Pronatec atenderá prioritariamente:<br />I - estudantes do ensino médio da rede pública, inclusive da educação de jovens e adultos;<br />II - trabalhadores;<br />III - beneficiários dos programas federais de transferência de renda; e<br />IV - estudante que tenha cursado o ensino médio completo em escola da rede pública ou em instituições privadas na condição de bolsista integral, nos termos do regulamento.<br />§ 1º Entre os trabalhadores a que se refere o inciso II, incluem-se os agricultores familiares, silvicultores, aquicultores, extrativistas e pescadores.<br />§ 2º Será estimulada a participação das pessoas com deficiência nas ações de educação profissional e tecnológica desenvolvidas no âmbito do Pronatec, observadas as condições de acessibilidade e participação plena no ambiente educacional, tais como adequação de equipamentos, de materiais pedagógicos, de currículos e de estrutura física.<br />§ 3º As ações desenvolvidas no âmbito do Pronatec contemplarão a participação de povos indígenas, comunidades quilombolas e adolescentes e jovens em cumprimento de medidas socioeducativas.<br />§ 4º Será estimulada a participação de mulheres responsáveis pela unidade familiar beneficiárias de programas federais de transferência de renda, nos cursos oferecidos por intermédio da Bolsa-Formação. (Incluído pela Lei nº 12.816, de 2013)', null);
