INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (284, 'Política Nacional de Incentivo à Ovinocaprinocultura', '2019-01-01', 'Não se aplica', 'Não se aplica', '13854/2019', '2019-07-09', null, 'Art. 1o  Fica instituída a Política Nacional de Incentivo à Ovinocaprinocultura, com o objetivo de promover: I - o aumento da escala da produção da ovinocaprinocultura;  II - a intensificação do manejo, com a eficiência da produtividade e da rentabilidade; III - a regularidade do fornecimento e a padronização da produção da ovinocaprinocultura; IV - a melhora da qualidade dos produtos oferecidos ao consumidor, a segurança alimentar e o combate ao abigeato, por meio da regularização do abate e do comércio de produtos da ovinocaprinocultura;  V - o estímulo ao processamento industrial, familiar e artesanal dos produtos oriundos de ovinos e caprinos; VI - a pesquisa e a assistência técnica e extensão rural, para a modernização tecnológica e de gestão das cadeias produtivas de ovinos e caprinos; VII - o melhoramento genético dos animais, com o desenvolvimento de raças mais produtivas, adaptadas e capazes de gerar produtos de melhor padrão de qualidade para o consumidor; VIII - a organização da produção; IX - os investimentos produtivos direcionados ao atendimento das demandas do mercado de ovinos e caprinos; e X - a articulação setorial, com o desenvolvimento de redes de cooperação econômica e tecnológica. Parágrafo único. Para os fins desta Lei, ovinocaprinocultura refere-se à criação de ovinos e caprinos com a finalidade de produção de carne, lã, couro, leite e outros derivados.', '#N/A', 'Política sem ação orçamentária associada', 'Lei ordinária', 4, 3, 4, null, null, 'Parágrafo único. Para os fins desta Lei, ovinocaprinocultura refere-se à criação de ovinos e caprinos com a finalidade de produção de carne, lã, couro, leite e outros derivados. ', null);
