INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (298, 'Política Nacional de Recursos Hídricos', '1997-01-01', 'Não se aplica', 'Não se aplica', '9433/1997', '1997-01-09', null, 'Art. 2º São objetivos da Política Nacional de Recursos Hídricos: I - assegurar à atual e às futuras gerações a necessária disponibilidade de água, em padrões de qualidade adequados aos respectivos usos; II - a utilização racional e integrada dos recursos hídricos, incluindo o transporte aquaviário, com vistas ao desenvolvimento sustentável; III - a prevenção e a defesa contra eventos hidrológicos críticos de origem natural ou decorrentes do uso inadequado dos recursos naturais. IV - incentivar e promover a captação, a preservação e o aproveitamento de águas pluviais. (Incluído pela Lei nº 13.501, de 2017)', 'Alterações no objetivo', 'Política com ação orçamentária associada', 'Lei ordinária', 4, 4, 13, null, null, 'Vegetação nativa', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (309, 'Política Nacional do Livro', '2003-01-01', 'Não se aplica', 'Não se aplica', '10753/2003', '2003-10-31', null, 'Art. 1o Esta Lei institui a Política Nacional do Livro, mediante as seguintes diretrizes: I - assegurar ao cidadão o pleno exercício do direito de acesso e uso do livro;  II - o livro é o meio principal e insubstituível da difusão da cultura e transmissão do conhecimento, do fomento à pesquisa social e científica, da conservação do patrimônio nacional, da transformação e aperfeiçoamento social e da melhoria da qualidade de vida;   III - fomentar e apoiar a produção, a edição, a difusão, a distribuição e a comercialização do livro; IV - estimular a produção intelectual dos escritores e autores brasileiros, tanto de obras científicas como culturais; V - promover e incentivar o hábito da leitura; VI - propiciar os meios para fazer do Brasil um grande centro editorial; VII - competir no mercado internacional de livros, ampliando a exportação de livros nacionais; VIII - apoiar a livre circulação do livro no País; IX - capacitar a população para o uso do livro como fator fundamental para seu progresso econômico, político, social e promover a justa distribuição do saber e da renda; X - instalar e ampliar no País livrarias, bibliotecas e pontos de venda de livro;
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (332, 'Programa de Apoio ao Desenvolvimento Institucional do Sistema Único de Saúde (PROADI/SUS)', '2009-01-01', 'Não se aplica', 'Não se aplica', '12101/2009', '2009-11-27', null, 'Art. 11.  A entidade de saúde de reconhecida excelência poderá, alternativamente, para dar cumprimento ao requisito previsto no art. 4o, realizar projetos de apoio ao desenvolvimento institucional do SUS, celebrando ajuste com a União, por intermédio do Ministério da Saúde, nas seguintes áreas de atuação: I - estudos de avaliação e incorporação de tecnologias; II - capacitação de recursos humanos; III - pesquisas de interesse público em saúde; ou IV - desenvolvimento de técnicas e operação de gestão em serviços de saúde. ', 'http://www.planalto.gov.br/ccivil_03/_ato2007-2010/2009/lei/l12101.htm; http://www.planalto.gov.br/ccivil_03/_ato2011-2014/2014/decreto/d8242.htm; https://bvsms.saude.gov.br/bvs/saudelegis/gm/2014/prt2814_22_12_2014.html', '#N/A', 'Lei ordinária', 5, 1, 1, null, null, 'Art. 13.  É beneficiária do PATVD a pessoa jurídica que realize investimento em pesquisa e desenvolvimento – P&D na forma do art. 17 desta Lei e que exerça as atividades de desenvolvimento e fabricação de equipamentos transmissores de sinais por radiofreqüência para televisão digital, classificados no código 8525.50.2 da NCM.', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (338, 'Programa de Bolsas para a Educação pelo Trabalho', '2011-01-01', 'Não se aplica', 'Não se aplica', '12513/2011', '2005-06-30', null, 'O Programa de Bolsas para a Educação pelo Trabalho é uma estratégia para provimento e fixação de profissionais em programas, projetos, ações e atividades em regiões prioritárias para o Sistema Único de Saúde (SUS), destinado aos estudantes de graduação dos cursos da área da saúde, profissionais de nível superior e trabalhadores da área da saúde e tem como objetivo promover a vivência, estágios, aperfeiçoamentos, formação e especialização de nível superior e médio em áreas prioritárias. (Origem: PRT MS/GM 1111/2005, Art. 2º) (com redação dada pela PRT MS/GM 754/2012)', 'O Programa foi criado por lei de 2005, que contava com assinatura do Ministro da Saúde. A lei de 2011 não foi assinada pelo ministro da Saúde.  http://www.planalto.gov.br/ccivil_03/_ato2004-2006/2005/lei/l11129.htm; http://www.planalto.gov.br/ccivil_03/_Ato2011-2014/2011/Lei/L12513.htm#art16; Contudo, portarias do Ministério da Saúde regulamentam o Programa e até falam que seus recursos orçamentários são do MS: https://bvsms.saude.gov.br/bvs/saudelegis/gm/2005/prt1111_05_07_2005.html; https://bvsms.saude.gov.br/bvs/saudelegis/gm/2012/prt0754_18_04_2012.html. ', 'Ação 20YD', 'Lei ordinária', 5, 1, 1, null, null, 'Art. 15. É instituído o Programa de Bolsas para a Educação pelo Trabalho, destinado aos estudantes de educação superior, prioritariamente com idade inferior a 29 (vinte e nove) anos, e aos trabalhadores da área da saúde, visando à vivência, ao estágio da área da saúde, à educação profissional técnica de nível médio, ao aperfeiçoamento e à especialização em área profissional, como estratégias para o provimento e a fixação de profissionais em programas, projetos, ações e atividades e em regiões prioritárias para o Sistema Único de Saúde. (Redação dada pela Lei nº 12.513, de 2011)', null);
