INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (422, 'Programa Nacional de Renda Mínima', '2001-01-01', '2140-1/2001', '15 de março de 2001', '10219/2001', '2001-04-12', null, '§ 1o O programa criado nos termos do caput deste artigo constitui o instrumento de participação financeira da União em programas municipais de garantia de renda mínima associados a ações socioeducativas, sem prejuízo da diversidade dos programas municipais.', '#N/A', 'Programa sem ação orçamentária associada', 'Lei ordinária', 5, 1, 6, null, null, 'Art. 2o A partir do exercício de 2001, a União apoiará programas de garantia de renda mínima associados a ações socioeducativas, que preencham, cumulativamente, os seguintes requisitos:<br />I - sejam instituídos por lei municipal, compatível com o termo de adesão referido no inciso I do art. 5o;<br />II - tenham como beneficiárias as famílias residentes no Município, com renda familiar per capita inferior ao valor fixado nacionalmente em ato do Poder Executivo para cada exercício e que possuam sob sua responsabilidade crianças com idade entre seis e quinze anos, matriculadas em estabelecimentos de ensino fundamental regular, com freqüência escolar igual ou superior a oitenta e cinco por cento;<br />III - incluam iniciativas que, diretamente ou em parceria com instituições da comunidade, incentivem e viabilizem a permanência das crianças beneficiárias na rede escolar, por meio de ações socioeducativas de apoio aos trabalhos escolares, de alimentação e de práticas desportivas e culturais em horário complementar ao das aulas; e<br />IV - submetam-se ao acompanhamento de um conselho de controle social, designado ou constituído para tal finalidade, composto por representantes do poder público e da sociedade civil, observado o disposto no art. 8o.', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (426, 'Programa Permanente de Combate à Seca (PROSECA)', '2003-01-01', 'Não se aplica', 'Não se aplica', '10638/2003', '2003-01-07', null, 'Art. 1o Fica o Poder Executivo autorizado a instituir o Programa Permanente de Combate à Seca – PROSECA, com os seguintes objetivos: I – realização de estudo detalhado de todas as disponibilidades hídricas locais do Semi-árido do Nordeste; II – identificação de alternativas de complementação da demanda hídrica do Semi-árido do Nordeste; III – implementação de ações imediatas destinadas à eliminação do déficit hídrico do Semi-árido setentrional do Nordeste; IV – implementação de projeto permanente de utilização otimizada e sustentada dos recursos hídricos locais do Semi-árido do Nordeste;
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (506, 'Política de Patrimônio Cultural Material do Iphan', '2018-01-01', 'Não se aplica', 'Não se aplica', '375/2018', '2018-09-19', null, 'Art. 4º A PPCM tem caráter orientador, estratégico e operacional, e busca promover de forma coerente e concertada a preservação do patrimônio cultural material. Art. 5º São objetivos específicos do PPCM: I. Qualificar, ampliar e integrar as ações e atividades de preservação do patrimônio cultural material; II. Estabelecer práticas para a elaboração participativa dos instrumentos de preservação, de forma a ampliar a legitimidade perante os grupos sociais locais e agentes públicos e facilitar a definição de estratégias de gestão compartilhada do patrimônio cultural material; III. Considerar as práticas e instrumentos da preservação desenvolvidos ou sugeridos pelo Comitê do Patrimônio Mundial e pela Comissão Cultural do Mercosul; IV. Precisar os entendimentos institucionais visando a definição de conceitos específicos aplicáveis à preservação do patrimônio cultural material; e V. Fortalecer a preservação do patrimônio cultural material de povos e comunidades tradicionais, enquanto grupos portadores de referência à identidade, à ação e à memória do país.', '#N/A', 'Política com ação orçamentária associada', 'Portaria', 4, 1, 18, null, null, 'Parágrafo único. Por patrimônio cultural material entende-se o universo de bens tangíveis, móveis ou imóveis, tomados individualmente ou em conjunto, portadores de referência à identidade, à ação, à memória dos diferentes grupos formadores da sociedade brasileira.', null);
