INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (329, 'Programa de Alimentação do Trabalhador (PAT)', '1976-01-01', 'Não se aplica', 'Não se aplica', '6321/1976', '1976-04-14', null, 'Art 1º As pessoas jurídicas poderão deduzir, do lucro tributável para fins do imposto sobre a renda o dobro das despesas comprovadamente realizadas no período base, em programas de alimentação do trabalhador, previamente aprovados pelo Ministério do Trabalho na forma em que dispuser o Regulamento desta Lei. ', '#N/A', '#N/A', 'Lei ordinária', 5, 1, 21, null, null, 'Art 2º Os programas de alimentação a que se refere o artigo anterior deverão conferir prioridade ao atendimento dos trabalhadores de baixa renda e limitar-se-ão aos contratados pela pessoa jurídica beneficiária.', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (495, 'Programa Viaja Mais Melhor Idade', '2013-01-01', 'Não se aplica', 'Não se aplica', 'Não se aplica', null, null, 'duplo objetivo. Primeiro, fazer a inclusão social por meio do turismo, viabilizando o acesso do público idoso às viagens de lazer. Depois, estimular o turismo interno, ao fazer com que aposentados, pensionistas e maiores de 60 anos aproveitem seu tempo livre para viajar pelo Brasil. ', 'http://www.servicos.ms.gov.br/fundtur/ViajaMaisMelhorIdade/Cartilha%20do%20Viaja%20Mais%20Melhor%20Idade.pdf', '#N/A', 'Não se aplica', 5, 3, 16, null, null, 'Art. 2º Fica instituído o Projeto Viaja Mais Melhor Idade, que tem por finalidade promover a inclusão social dos idosos, com idade a partir de sessenta anos, dos aposentados e dos pensionistas, proporcionando-lhes oportunidades de viajar em condições favoráveis e de usufruir os benefícios da atividade turística, contribuindo para o fortalecimento do setor de turismo no Brasil.', null);
