INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (328, 'Programa de Administração Patrimonial Imobiliária da União (PROAP)', '1998-01-01', 'Não se aplica', 'Não se aplica', '9636/1998', '1998-05-15', null, 'Art. 37. É instituído o Programa de Administração Patrimonial Imobiliária da União - PROAP, destinado ao incentivo à regularização, administração, aforamento, alienação e fiscalização de bens imóveis de domínio da União, ao incremento das receitas patrimoniais, bem como à modernização e informatização dos métodos e processos inerentes à Secretaria do Patrimônio da União.
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (516, 'Política Nacional de Atenção Básica', '2011-01-01', 'Não se aplica', 'Não se aplica', '2488/2011', '2011-10-21', '2017-09-21', 'A Atenção Básica caracteriza-se por um conjunto de ações de saúde, no âmbito individual e coletivo, que abrange a promoção e a proteção da saúde, a prevenção de agravos, o diagnóstico, o tratamento, a reabilitação, redução de danos e a manutenção da saúde com o objetivo de desenvolver uma atenção integral que impacte na situação de saúde e autonomia das pessoas e nos determinantes econdicionantes de saúde das coletividades. É desenvolvida por meio do exercício de práticas de cuidado e gestão, democráticas e participativas, sob forma de trabalho em equipe, dirigidas a populações de territórios definidos, pelas quais assume a responsabilidade sanitária, considerando a dinamicidade existente no território em que vivem essas populações. Utiliza tecnologias de cuidado complexas e variadas que devem auxiliar no manejo das demandas e necessidades de saúde de maior freqüência e relevância em seu território, observando critérios de risco, vulnerabilidade, resiliência e o imperativo ético de que toda demanda, necessidade de saúde ou sofrimento devem ser acolhidos.', 'Modificada pela PNAB 2017', 'Política com ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'Sem especificação  População em geral', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (339, 'Programa de Ciência e Tecnologia para o Agronegócio', '2001-01-01', 'Não se aplica', 'Não se aplica', '10332/2001', '2001-12-20', null, 'Art. 2o Os Programas referidos no art. 1o desta Lei, previstos na Lei no 9.989, de 21 de julho de 2000, objetivam incentivar o desenvolvimento científico e tecnológico brasileiro, por meio de financiamento de atividades de pesquisa e desenvolvimento científico-tecnológico de interesse das áreas do agronegócio, da saúde, da biotecnologia e recursos genéticos, do setor aeronáutico e da inovação para a competitividade.', '#N/A', 'Programa com ação orçamentária associada', 'Lei ordinária', 5, 3, 9, null, null, 'Universidades e centros de pesquisas', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (519, 'Política Nacional de Atenção em Oftalmologia', '2008-01-01', 'Não se aplica', 'Não se aplica', '957/2008', '2008-05-15', null, 'Art. 2º - Estabelecer que a Política Nacional de Atenção em Oftalmologia seja organizada de forma articulada entre o Ministério da Saúde, as Secretarias de Estado da Saúde e do Distrito Federal e as Secretarias Municipais de Saúde, por intermédio de redes estaduais e regionais, permitindo: I - desenvolver estratégias de promoção da qualidade de vida, educação, proteção e recuperação da saúde e prevenção de danos, protegendo e desenvolvendo a autonomia e a equidade de indivíduos e coletividades; II - organizar uma linha de cuidados integrais (promoção, prevenção, tratamento e recuperação) que perpasse todos os níveis de atenção, promovendo, dessa forma, a inversão do modelo de atenção; III - identificar os determinantes e condicionantes das principais patologias que levam à doença oftalmológica e desenvolver ações transetoriais de responsabilidade pública, sem excluir as responsabilidades de toda a sociedade; IV - definir critérios técnicos mínimos para o funcionamento e avaliação das Unidades de Atenção Especializada, públicas ou privadas que prestam atenção em oftalmologia, bem como os mecanismos de sua monitorização com vistas à diminuição dos riscos aos quais fica exposto o paciente com doença oftalmológica; V - ampliar a cobertura no atendimento aos pacientes com doenças oftalmológicas no Brasil, garantindo a universalidade, a eqüidade, a integralidade, o controle social e o acesso às Unidades de Atenção Especializada em Oftalmologia; VI - contribuir para o desenvolvimento de processos e métodos de coleta, análise e organização dos resultados das ações decorrentes da Política Nacional de Atenção em Oftalmologia, permitindo que a partir de seu desempenho seja possível um aprimoramento da gestão, disseminação das informações e uma visão dinâmica do estado de saúde das pessoas com doenças oftalmológicas; VII - promover intercâmbio com outros subsistemas de informações setoriais, implementando e aperfeiçoando permanentemente a produção de dados e garantindo a democratização das informações;e VIII - qualificar a assistência e promover a educação permanente dos profissionais de saúde envolvidos com a implantação e implementação da Política Nacional de Atenção em Oftalmologia, em acordo com os princípios da integralidade e da humanização.', '#N/A', 'Política sem ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'paciente portador de doenças oftalmológicas a procedimentos de média e alta complexidade', null);
