INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (376, 'Programa de Volta pra Casa', '2003-01-01', 'Não se aplica', 'Não se aplica', '10708/2003', '2003-08-01', null, 'Art. 1o Fica instituído o auxílio-reabilitação psicossocial para assistência, acompanhamento e integração social, fora de unidade hospitalar, de pacientes acometidos de transtornos mentais, internados em hospitais ou unidades psiquiátricas, nos termos desta Lei.', 'http://www.planalto.gov.br/ccivil_03/leis/2003/l10.708.htm', 'Ação 20AI', 'Lei ordinária', 5, 1, 1, null, null, 'Art. 1o Parágrafo único. O auxílio é parte integrante de um programa de ressocialização de pacientes internados em hospitais ou unidades psiquiátricas, denominado "De Volta Para Casa", sob coordenação do Ministério da Saúde. Art. 3o São requisitos cumulativos para a obtenção do benefício criado por esta Lei que: I - o paciente seja egresso de internação psiquiátrica cuja duração tenha sido, comprovadamente, por um período igual ou superior a dois anos; II - a situação clínica e social do paciente não justifique a permanência em ambiente hospitalar, indique tecnicamente a possibilidade de inclusão em programa de reintegração social e a necessidade de auxílio financeiro; III - haja expresso consentimento do paciente, ou de seu representante legal, em se submeter às regras do programa; IV - seja garantida ao beneficiado a atenção continuada em saúde mental, na rede de saúde local ou regional.  § 1o O tempo de permanência em Serviços Residenciais Terapêuticos será considerado para a exigência temporal do inciso I deste artigo. § 2o Para fins do inciso I, não poderão ser considerados períodos de internação os de permanência em orfanatos ou outras instituições para menores, asilos, albergues ou outras instituições de amparo social, ou internações em hospitais psiquiátricos que não tenham sido custeados pelo Sistema Único de Saúde - SUS ou órgãos que o antecederam e que hoje o compõem. § 3o Egressos de Hospital de Custódia e Tratamento Psiquiátrico poderão ser igualmente beneficiados, procedendo-se, nesses casos, em conformidade com a decisão judicial. ', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (385, 'Programa Emergencial de Retomada do Setor de Eventos (PERSE)', '2021-01-01', 'Não se aplica', 'Não se aplica', '14148/2021', '2021-05-04', null, 'Art. 2º Fica instituído o Programa Emergencial de Retomada do Setor de Eventos (Perse), com o objetivo de criar condições para que o setor de eventos possa mitigar as perdas oriundas do estado de calamidade pública reconhecido pelo Decreto Legislativo nº 6, de 20 de março de 2020.', '#N/A', '#N/A', 'Lei ordinária', 5, 3, 12, null, null, '§ 1º Para os efeitos desta Lei, consideram-se pertencentes ao setor de eventos as pessoas jurídicas, inclusive entidades sem fins lucrativos, que exercem as seguintes atividades econômicas, direta ou indiretamente:<br />I - realização ou comercialização de congressos, feiras, eventos esportivos, sociais, promocionais ou culturais, feiras de negócios, shows, festas, festivais, simpósios ou espetáculos em geral, casas de eventos, buffets sociais e infantis, casas noturnas e casas de espetáculos;<br />II - hotelaria em geral;<br />III - administração de salas de exibição cinematográfica; e<br />IV - prestação de serviços turísticos, conforme o art. 21 da Lei nº 11.771, de 17 de setembro de 2008.', null);
