INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (538, 'Política Nacional de Gestão de Tecnologias em Saúde', '2009-01-01', 'Não se aplica', 'Não se aplica', '2690/2009', '2009-11-05', null, 'Art. 3º É objetivo geral da Política Nacional de Gestão de Tecnologias de Saúde maximizar os benefícios de saúde a serem obtidos com os recursos disponíveis, assegurando o acesso da população a tecnologias efetivas e seguras, em condições de equidade, visando: I - orientar os processos de incorporação de tecnologias nos sistemas e serviços de saúde; II - nortear a institucionalização dos processos de avaliação e de incorporação de tecnologias baseados na análise das consequências e dos custos para o sistema de saúde e para a população; III - promover o uso do conhecimento técnico-científico atualizado no processo de gestão de tecnologias em saúde; IV - sensibilizar os profissionais de saúde e a sociedade em geral para a importância das consequências econômicas e sociais do uso inapropriado de tecnologias nos sistemas e serviços de saúde; e V - fortalecer o uso de critérios e processos de priorização da incorporação de tecnologias, considerando aspectos de efetividade, necessidade, segurança, eficiência e equidade.', '#N/A', 'Política com ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'Sistemas e serviços de saúde', null);
