INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (552, 'Política Nacional de Saúde Integral das Populações do Campo, da Floresta e das Águas (PNSIPCFA)', '2011-01-01', 'Não se aplica', 'Não se aplica', '2866/2011', '2011-12-02', null, 'Art. 3º A PNSIPCF tem os seguintes objetivos específicos: I - garantir o acesso aos serviços de saúde com resolutividade, qualidade e humanização, incluindo as ações de atenção, as especializadas de média e alta complexidade e as de urgência e de emergência, de acordo com as necessidades e demandas apontadas pelo perfil epidemiológico da população atendida; II - contribuir para a redução das vulnerabilidades em saúde das populações do campo e da floresta, desenvolvendo ações integrais voltadas para a saúde do idoso, da mulher, da pessoa com deficiência, da criança e do adolescente, do homem e do trabalhador, considerando a saúde sexual e reprodutiva, bem como a violência sexual e doméstica; III - reduzir os acidentes e agravos relacionados aos processos de trabalho no campo e na floresta, particularmente o adoecimento decorrente do uso de agrotóxicos e mercúrio, o advindo do risco ergonômico do trabalho no campo e na floresta e da exposição contínua aos raios ultravioleta; IV - contribuir para a melhoria da qualidade de vida das populações do campo e da floresta, incluindo articulações intersetoriais para promover a saúde, envolvendo ações de saneamento e meio ambiente, especialmente para a redução de riscos sobre a saúde humana; V - reconhecer e valorizar os saberes e as práticas tradicionais de saúde das populações do campo e da floresta, respeitando suas especificidades; VI - promover planejamentos participativos capazes de identificar as demandas de saúde das populações do campo e da floresta e definir metas, estratégias e ações específicas para sua atenção; VII - incluir no processo de educação permanente dos trabalhadores de saúde as temáticas e os conteúdos relacionados às necessidades, demandas e especificidades das populações do campo e da floresta, considerando a interculturalidade na atenção aos povos e comunidades tradicionais; VIII - apoiar processos de educação e informação das populações do campo e da floresta sobre o direito à saúde; IX - apoiar a expansão da participação das representações dessas populações nos Conselhos de Saúde estaduais, distrital e municipais e em outros espaços de gestão participativa; X - promover mecanismos de informação e comunicação, de acordo com a diversidade e as especificidades socioculturais; XI - incentivar a pesquisa e a produção de conhecimento sobre os riscos, a qualidade de vida e a saúde das populações do campo e da floresta, respeitando as especificidades de geração, raça/cor, gênero, etnia e orientação sexual; e XII - promover o fortalecimento e a ampliação do sistema público de vigilância em saúde, do monitoramento e da avaliação tecnológica sobre os agravos à saúde decorrentes do uso de agrotóxicos e transgênicos.', '#N/A', 'Política com ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'Pessoas com deficiência', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (530, 'Política Nacional de Atenção Obstétrica e Neonatal', '2005-01-01', 'Não se aplica', 'Não se aplica', '1067/2005', '2005-07-04', null, 'Parágrafo único. A Política Nacional de Atenção Obstétrica e Neonatal será executada conjuntamente pelo Ministério da Saúde e as Secretarias de Saúde dos Estados, dos Municípios e do Distrito Federal e tem por objetivo o desenvolvimento de ações de promoção, prevenção e assistência à saúde de gestantes e recém-nascidos, promovendo a ampliação do acesso a essas ações, o incremento da qualidade da assistência obstétrica e neonatal, bem como sua organização e regulação no âmbito do Sistema Único de Saúde.', '#N/A', 'Política sem ação orçamentária associada', 'Portaria', 4, 1, 31, null, '2023-03-08 11:49:57', 'gestantes e recém-nascidos', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (458, 'Política Nacional de Qualificação no Turismo', '2017-01-01', 'Não se aplica', 'Não se aplica', 'Não se aplica', null, null, 'Qualificação no Turismo partiu da decisão do Ministério do Turismo de fomentar o turismo por meio da qualificação profissional ', 'https://www.gov.br/turismo/pt-br/centrais-de-conteudo/pnqt-pdf', '#N/A', 'Não se aplica', 4, 3, 16, null, null, 'Art. 3º A PNPDEC abrange as ações de prevenção, mitigação, preparação, resposta e recuperação voltadas à proteção e defesa civil.', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (566, 'Programa de Atenção Integral à Família (Paif)', '2004-01-01', 'Não se aplica', 'Não se aplica', '78/2004', '2004-04-08', null, 'Art.  2º  -  O  Programa  de  Atenção  Integral  à  Família  -  PAIF tem  como  objetivos: I.  contribuir  para  a  efetivação  da  Política  de  Assistência  Social  como  política  pública  garantidora  de  direitos  de cidadania  e  promotora  de  desenvolvimento  social,  na  perspectiva  da  prevenção  e  superação  das  desigualdades  e exclusão  social,  tendo  a  família  como  unidade  de  atenção  para  a  concepção  e  a  implementação  de  programas,  projetos, serviços  e  benefícios. II.  contribuir  para  superar  a  abordagem  fragmentada  e  individualizadora  dos  programas  tradicionais; III.  garantir  a  convivência  familiar  e  comunitária  dos  membros  das famílias; IV.  contribuir  para  o  processo  de  autonomia  e  emancipação  social  das famílias  e  seus  membros; V.  viabilizar  a  formação  para  a  cidadania; VI.  articular  e  integrar  ações públicas e  privadas em  rede; VII.  colaborar  com  a  descentralização  político-administrativa.', '#N/A', '#N/A', 'Portaria', 5, 1, 6, null, null, 'Art. 7°  Determinar que os objetivos descritos no artigo 5º desta Portaria sejam direcionados prioritariamente a pessoas que fazem uso prejudicial de álcool, em face da magnitude epidemiológica do seu uso e de suas conseqüências, porém, contemplando igualmente o conjunto de usuários de álcool e outras drogas que apresente a maior demanda por cuidados, no território de abrangência de cada serviço.', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (554, 'Política Nacional de Transportes', '2018-01-01', 'Não se aplica', 'Não se aplica', '235/2018', '2018-03-29', null, 'Art. 4° A Política Nacional de Transportes tem por objetivos: I - prover um sistema acessível, eficiente e confiável para a mobilidade de pessoas e bens; II - garantir a segurança operacional em todos os modos de transportes;
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (555, 'Política Nacional para a Prevenção e Controle do Câncer na Rede de Atenção à Saúde das Pessoas com Doenças Crônicas no âmbito do Sistema Único de Saúde', '2013-01-01', 'Não se aplica', 'Não se aplica', '874/2013', '2013-05-16', null, 'Art. 2º A Política Nacional para a Prevenção e Controle do Câncer tem como objetivo a redução da mortalidade e da incapacidade causadas por esta doença e ainda a possibilidade de diminuir a incidência de alguns tipos de câncer, bem como contribuir para a melhoria da qualidade de vida dos usuários com câncer, por meio de ações de promoção, prevenção, detecção precoce, tratamento oportuno e cuidados paliativos.', '#N/A', 'Política sem ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'Parágrafo único.  Para fins deste Decreto, considera-se população em situação de rua o grupo populacional heterogêneo que possui em comum a pobreza extrema, os vínculos familiares interrompidos ou fragilizados e a inexistência de moradia convencional regular, e que utiliza os logradouros públicos e as áreas degradadas como espaço de moradia e de sustento, de forma temporária ou permanente, bem como as unidades de acolhimento para pernoite temporário ou como moradia provisória.', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (556, 'Política Nacional para os Hospitais de Pequeno Porte', '2004-01-01', 'Não se aplica', 'Não se aplica', '1044/2004', '2004-06-01', null, 'Art. 1º  Instituir a Política Nacional para os Hospitais de Pequeno Porte, utilizando um modelo de organização e financiamento que estimule a inserção desses Hospitais de Pequeno Porte na rede hierarquizada de atenção à saúde, agregando resolutividade e qualidade às ações definidas para o seu nível de complexidade.', '#N/A', 'Política sem ação orçamentária associada', 'Portaria', 4, 1, 1, null, null, 'Sem especificação  População em geral', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (558, 'Programa Academia da Saúde', '2013-01-01', 'Não se aplica', 'Não se aplica', '2681/2013', '2011-04-07', null, 'Art. 2o O Programa Academia da Saúde tem como objetivo principal contribuir para a promoção da saúde e produção do cuidado e de modos de vida saudáveis da população a partir da implantação de polos com infraestrutura e profissionais qualificados. Art. 5o- São objetivos específicos do Programa Academia da Saúde: I - ampliar o acesso da população às políticas públicas de promoção da saúde; II - fortalecer a promoção da saúde como estratégia de produção de saúde; III - desenvolver a atenção à saúde nas linhas de cuidado, a fim de promover o cuidado integral; IV - promover práticas de educação em saúde; V - promover ações intersetoriais com outros pontos de atenção da Rede de Atenção à Saúde e outros equipamentos sociais do território; VI - potencializar as ações nos âmbitos da atenção básica, da vigilância em saúde e da promoção da saúde; VII - promover a integração multiprofissional na construção e na execução das ações; VIII - promover a convergência de projetos ou programas nos âmbitos da saúde, educação, cultura, assistência social, esporte e lazer; IX - ampliar a autonomia dos indivíduos sobre as escolhas de modos de vida mais saudáveis; X - aumentar o nível de atividade física da população; XI - promover hábitos alimentares saudáveis; XII - promover mobilização comunitária com a constituição de redes sociais de apoio e ambientes de convivência e solidariedade; XIII - potencializar as manifestações culturais locais e o conhecimento popular na construção de alternativas individuais e coletivas que favoreçam a promoção da saúde; e XIV - contribuir para ampliação e valorização da utilização dos espaços públicos de lazer, como proposta de inclusão social, enfrentamento das violências e melhoria das condições de saúde e qualidade de vida da população. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2013/prt2681_07_11_2013.html revoga a PORTARIA Nº 719/2011 https://bvsms.saude.gov.br/bvs/saudelegis/gm/2011/prt0719_07_04_2011.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'Art. 1º  Fica instituído o Programa Adote um Parque, com a finalidade de promover a conservação, a recuperação e a melhoria das unidades de conservação federais, por meio da participação de pessoas físicas e jurídicas privadas, nacionais e estrangeiras.', null);
