INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (612, 'Programa Nacional de Controle da Dengue (PNCD)', '2003-01-01', 'Não se aplica', 'Não se aplica', '1933/2003', '2003-10-09', null, 'Desenvolver ações permanentes de combate à dengue. Art. 1º Instituir o Programa Nacional de Controle da Dengue - PNCD, com as seguintes diretrizes: I - desenvolver campanhas de informação e de mobilização das pessoas, de maneira a criar-se uma maior responsabilização de cada família na manutenção de seu ambiente doméstico livre de potenciais criadouros do vetor; II - fortalecer a vigilância epidemiológica e entomológica para ampliar a capacidade de predição e de detecção precoce de surtos da doença; III - melhorar a qualidade do trabalho de campo de combate ao vetor; IV - integrar as ações de controle da dengue na atenção básica, com a mobilização do Programa de Agentes Comunitários de Saúde e Programa de Saúde da Família; V - utilizar instrumentos legais que facilitem o trabalho do poder público na eliminação de criadouros em imóveis comerciais, casas abandonadas, dentre outros; VI - atuar multisetorialmente por meio do fomento à destinação adequada de resíduos sólidos e a utilização de recipientes seguros para armazenagem de água; VI - desenvolver instrumentos mais eficazes de acompanhamento e supervisão das ações desenvolvidas pelo Ministério da Saúde, Estados e Municípios. ', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2003/prt1933_09_10_2003.html', '#N/A', 'Portaria', 5, 1, 1, null, null, 'População em geral ', null);
