INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (604, 'Programa Nacional de Atenção Comunitária Integrada a Usuários de Álcool e Outras Drogas', '2002-01-01', 'Não se aplica', 'Não se aplica', '816/2002', '2002-04-30', null, 'Art. 1º Instituir, no âmbito do Sistema Único de Saúde, o Programa Nacional de Atenção Comunitária Integrada a Usuários de Álcool e Outras Drogas, a ser desenvolvido de forma articulada pelo Ministério da Saúde e pelas Secretarias de Saúde dos estados, Distrito Federal e municípios, tendo por objetivos: I – Articular as ações desenvolvidas pelas três esferas de governo destinadas a promover a atenção aos pacientes com dependência e/ou uso prejudicial de álcool ou outras drogas; II - Organizar e implantar rede estratégica de serviços extra-hospitalares de atenção aos pacientes com esse tipo de transtorno, articulada à rede de atenção psicossocial; III - Aperfeiçoar as intervenções preventivas como forma de reduzir os danos sociais e à saúde representados pelo uso prejudicial de álcool e outras drogas; IV - Realizar ações de atenção/assistência aos pacientes e familiares, de forma integral e abrangente, com atendimento individual, em grupo, atividades comunitárias, orientação profissional, suporte medicamentoso,  psicoterápico, de orientação e outros; V - Organizar/regular as demandas e os fluxos assistenciais; VI - Promover, em articulação com instituições formadoras, a capacitação e supervisão das equipes de atenção básica, serviços e programas de saúde mental locais.', 'https://bvsms.saude.gov.br/bvs/saudelegis/gm/2002/prt0816_30_04_2002.html', 'Ação 0844 (2004), 8529 (2005 a 2007) e 8585 para custeio (e ações anteriores, da MAC, gestão plena etc)', 'Portaria', 5, 1, 1, null, null, 'Art. 1º  I – Articular as ações desenvolvidas pelas três esferas de governo destinadas a promover a atenção aos pacientes com dependência e/ou uso prejudicial de álcool ou outras drogas;', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (252, 'Brasil Carinhoso', '2012-01-01', '570/2012', '14 de maio de 2012', '12722/2012', '2012-10-03', '2012-07-03', 'Art. 4º São obrigatórias as transferências de recursos da União aos Municípios e ao Distrito Federal, desde que cumpridos os critérios de elegibilidade estabelecidos nesta Lei, com a finalidade de prestar apoio financeiro suplementar à manutenção e ao desenvolvimento da educação infantil para o atendimento em creches de crianças de zero a quarenta e oito meses cadastradas no Censo Escolar da Educação Básica e que sejam:  I - de famílias beneficiárias do Programa Bolsa Família, na forma estabelecida pela Lei nº 10.836, de 9 de janeiro de 2004 ; ou (Incluído pela Lei nº 13.348, de 2016);  II - beneficiárias do Benefício de Prestação Continuada - BPC, na forma estabelecida pela Lei nº 8.742, de 7 de dezembro de 1993 ; ou (Incluído pela Lei nº 13.348, de 2016); III - pessoas com deficiência, ainda que não se enquadrem nos incisos I ou II. (Incluído pela Lei nº 13.348, de 2016)', 'http://www.planalto.gov.br/ccivil_03/_ato2011-2014/2012/lei/l12722.htm', '#N/A', 'Lei ordinária', 5, 1, 6, null, null, 'beneficiários bolsa família, do BPC e pessoas com deficiência', null);
