INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (407, 'Programa Nacional de Apoio ao Transporte do Escolar (PNATE)', '2004-01-01', '173/2004', '17 de março de 2004', '10880/2004', '2004-06-11', '2012-07-16', 'Art. 2º Fica instituído o Programa Nacional de Apoio ao Transporte do Escolar - PNATE, no âmbito do Ministério da Educação, a ser executado pelo Fundo Nacional de Desenvolvimento da Educação - FNDE, com o objetivo de oferecer transporte escolar aos alunos do ensino fundamental público, residentes em área rural, por meio de assistência financeira, em caráter suplementar, aos Estados, ao Distrito Federal e aos Municípios, observadas as disposições desta Lei. (Redação dada pela Lei nº 11.947, de 2009)', 'alterações no objetivo', 'Programa com ação orçamentária associada', 'Lei ordinária', 5, 1, 19, null, null, 'Art. 2º  O Pronampe é destinado às pessoas a que se referem os incisos I e II do caput do art. 3º da Lei Complementar nº 123, de 14 de dezembro de 2006, considerada a receita bruta auferida no exercício de 2019. [I - no caso da microempresa, aufira, em cada ano-calendário, receita bruta igual ou inferior a R$ 360.000,00 (trezentos e sessenta mil reais; e II - no caso de empresa de pequeno porte, aufira, em cada ano-calendário, receita bruta superior a R$ 360.000,00 (trezentos e sessenta mil reais) e igual ou inferior a R$ 4.800.000,00 (quatro milhões e oitocentos mil reais).                     (Redação dada pela Lei Complementar nº 155, de 2016)     Produção de efeito]', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (416, 'Programa Nacional de Financiamento da Ampliação e Modernização da Frota Pesqueira Nacional (Profrota Pesqueira)', '2004-01-01', '140/2003', '26 de novembro de 2003', '10849/2004', '2004-03-24', '2004-03-08', 'Art. 2º O Profrota Pesqueira compreende financiamentos para a aquisição, construção, conversão, modernização, adaptação e equipagem de embarcações pesqueiras com o objetivo de reduzir a pressão de captura sobre estoques sobreexplotados, proporcionar a eficiência e sustentabilidade da frota pesqueira costeira e continental, promover o máximo aproveitamento das capturas, aumentar a produção pesqueira nacional, utilizar estoques pesqueiros na Zona Econômica Exclusiva brasileira e em águas internacionais, consolidar a frota pesqueira oceânica nacional e melhorar a qualidade do pescado produzido no Brasil. Art. 2º O Profrota Pesqueira compreende financiamentos para a aquisição, construção, conversão, modernização, substituição, adaptação e equipagem de embarcações pesqueiras com o objetivo de reduzir a pressão de captura sobre estoques sobre-explotados, proporcionar a eficiência e sustentabilidade da frota pesqueira costeira e continental, promover o máximo aproveitamento das capturas, aumentar a produção pesqueira nacional, utilizar estoques pesqueiros na Zona Econômica Exclusiva brasileira e em águas internacionais, consolidar a frota pesqueira oceânica nacional e melhorar a qualidade do pescado produzido no Brasil. (Redação dada pela Lei nº 12.712, de 2012.)', 'alterações no objetivo o publico alvo', 'Programa com ação orçamentária associada', 'Lei ordinária', 5, 4, 13, null, null, 'Parágrafo único. As modalidades referenciadas para a frota costeira e continental no caput deste artigo vinculam-se à diretriz de redução da pesca de espécies sobreexplotadas e envolvem duas linhas de financiamentos:<br />I - conversão e adaptação: consiste no aparelhamento de embarcações oriundas da captura de espécies oficialmente sobreexplotadas para a captura de espécies cujos estoques suportem aumento de esforço com abdicação da licença original;<br />II - substituição de embarcações: visa à substituição de embarcações e equipamentos de pesca tecnicamente obsoletos, com ou sem transferência de atividade sobreexplotada, por novas embarcações e apetrechos que em quaisquer das hipóteses impliquem redução de impactos sobre espécies com estoques saturados ou em processo de saturação e que resultem em melhores condições laborais.<br /><br />Parágrafo único. São beneficiárias do Profrota Pesqueira as pessoas físicas e jurídicas, inclusive cooperativas e associações, devidamente inscritas no Registro Geral da Atividade Pesqueira - RGP nas categorias de Armador de Pesca, Pescador Profissional, Indústria ou Empresa Pesqueira, classificadas por porte, conforme critérios a serem definidos em regulamento. (Redação dada pela Lei nº 12.712, de 2012.)', null);
