INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (94, 'Programa de Apoio a Planos de Reestruturação e Expansão das Universidades Federais (REUNI)', '2007-01-01', 'Não se aplica', 'Não se aplica', '6096/2007', '2007-04-25', null, 'Art. 1o  Fica instituído o Programa de Apoio a Planos de Reestruturação e Expansão das Universidades Federais - REUNI, com o objetivo de criar condições para a ampliação do acesso e permanência na educação superior, no nível de graduação, pelo melhor aproveitamento da estrutura física e de recursos humanos existentes nas universidades federais.', '#N/A', 'Programa com ação orçamentária associada', 'Decreto', 5, 1, 19, null, null, 'Para efeito do presente Programa, conceitua-se como Núcleo de Excelência um grupo organizado de pesquisadores e técnicos de alto nível, em permanente interação, com reconhecida competência e tradição em suas áreas de atuação técnico-científica, capaz de funcionar como fonte geradora e transformadora de conhecimento científico-tecnológico para aplicações em programas e projetos de relevância para o desenvolvimento do País.<br />Como os Núcleos de Excelência são centrados em grupos de pesquisadores que atendam a essa orientação, não haverá necessariamente coincidência entre um Núcleo e a unidade administrativa que o sedia (universidade, faculdade, instituto, escola, departamento, laboratório, etc.), embora deva ficar clara sua vinculação e apoio institucionais.', null);
