INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (260, 'Política de controle da natalidade de cães e gatos', '2017-01-01', 'Não se aplica', 'Não se aplica', '13426/2017', '2017-03-31', null, 'Art. 1º O controle de natalidade de cães e gatos em todo o território nacional será regido de acordo com o estabelecido nesta Lei, mediante esterilização permanente por cirurgia, ou por outro procedimento que garanta eficiência, segurança e bem-estar ao animal.', '#N/A', 'Política com ação orçamentária associada ', 'Lei ordinária', 4, 1, 17, null, null, 'Art. 2º A esterilização de animais de que trata o art. 1º desta Lei será executada mediante programa em que seja levado em conta:<br />I - o estudo das localidades ou regiões que apontem para a necessidade de atendimento prioritário ou emergencial, em face da superpopulação, ou quadro epidemiológico; II - o quantitativo de animais a serem esterilizados, por localidade, necessário à redução da taxa populacional em níveis satisfatórios, inclusive os não domiciliados; e III - o tratamento prioritário aos animais pertencentes ou localizados nas comunidades de baixa renda.', null);
