INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (363, 'Programa de Modernização da Gestão e de Responsabilidade Fiscal do Futebol Brasileiro', '2015-01-01', '671/2015', '20 de março de 2015', '13155/2015', '2015-08-05', '2015-07-13', 'Art. 2º Fica criado o Programa de Modernização da Gestão e de Responsabilidade Fiscal do Futebol Brasileiro - PROFUT, com o objetivo de promover a gestão transparente e democrática e o equilíbrio financeiro das entidades desportivas profissionais de futebol.', '#N/A', 'Programa sem ação orçamentária associada', 'Lei ordinária', 5, 3, 16, null, null, 'Art. 3º A adesão ao Profut dar-se-á com o requerimento das entidades desportivas profissionais de futebol do parcelamento de que trata a Seção II deste Capítulo.<br />Parágrafo único. Para aderir ao Profut, as entidades desportivas profissionais de futebol deverão apresentar os seguintes documentos:<br />I - estatuto social ou contrato social e atos de designação e responsabilidade de seus gestores;<br />II - demonstrações financeiras e contábeis, nos termos da legislação aplicável; e<br />III - relação das operações de antecipação de receitas realizadas, assinada pelos dirigentes e pelo conselho fiscal.', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (289, 'Política Nacional de Irrigação', '2013-01-01', 'Não se aplica', 'Não se aplica', '12787/2013', '2013-01-11', null, 'Art. 4º A Política Nacional de Irrigação tem por objetivos: I - incentivar a ampliação da área irrigada e o aumento da produtividade em bases ambientalmente sustentáveis; II - reduzir os riscos climáticos inerentes à atividade agropecuária, principalmente nas regiões sujeitas a baixa ou irregular distribuição de chuvas; III - promover o desenvolvimento local e regional, com prioridade para as regiões com baixos indicadores sociais e econômicos; IV - concorrer para o aumento da competitividade do agronegócio brasileiro e para a geração de emprego e renda; V - contribuir para o abastecimento do mercado interno de alimentos, de fibras e de energia renovável, bem como para a geração de excedentes agrícolas para exportação;
