INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (297, 'Política Nacional de Proteção E Defesa Civil (PNPDEC)', '2012-01-01', 'Não se aplica', 'Não se aplica', '12608/2012', '2012-04-10', null, 'Art. 3º A PNPDEC abrange as ações de prevenção, mitigação, preparação, resposta e recuperação voltadas à proteção e defesa civil. Parágrafo único. A PNPDEC deve integrar-se às políticas de ordenamento territorial, desenvolvimento urbano, saúde, meio ambiente, mudanças climáticas, gestão de recursos hídricos, geologia, infraestrutura, educação, ciência e tecnologia e às demais políticas setoriais, tendo em vista a promoção do desenvolvimento sustentável.', '#N/A', 'NA', 'Lei ordinária', 4, 2, 5, null, null, '§ 1º Para os efeitos desta Lei, é considerada pessoa com transtorno do espectro autista aquela portadora de síndrome clínica caracterizada na forma dos seguintes incisos I ou II:<br />I - deficiência persistente e clinicamente significativa da comunicação e da interação sociais, manifestada por deficiência marcada de comunicação verbal e não verbal usada para interação social; ausência de reciprocidade social; falência em desenvolver e manter relações apropriadas ao seu nível de desenvolvimento;<br />II - padrões restritivos e repetitivos de comportamentos, interesses e atividades, manifestados por comportamentos motores ou verbais estereotipados ou por comportamentos sensoriais incomuns; excessiva aderência a rotinas e padrões de comportamento ritualizados; interesses restritos e fixos.', null);
