INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (325, 'Programa Casa Verde e Amarela', '2021-01-01', '996/2020', '25 de agosto de 2020', '14118/2021', '2021-01-13', '2021-01-13', 'Art. 1 º  É instituído o Programa Casa Verde e Amarela, com a finalidade de promover o direito à moradia a famílias residentes em áreas urbanas com renda mensal de até R$ 7.000,00 (sete mil reais) e a famílias residentes em áreas rurais com renda anual de até R$ 84.000,00 (oitenta e quatro mil reais), associado ao desenvolvimento econômico, à geração de trabalho e de renda e à elevação dos padrões de habitabilidade e de qualidade de vida da população urbana e rural.', '#N/A', '#N/A', 'Lei ordinária', 5, 1, 11, null, null, '§ 1º Na hipótese de contratação de operações de financiamento habitacional, a concessão de subvenções econômicas com recursos orçamentários da União fica limitada ao atendimento de famílias em áreas urbanas com renda mensal de até R$ 4.000,00 (quatro mil reais) e de agricultores e trabalhadores rurais em áreas rurais com renda anual de até R$ 48.000,00 (quarenta e oito mil reais).', null);
INSERT INTO catalogo.politica (id, nome, ano, medida_provisoria, medida_provisoria_inicio_vigencia, legislacao, vigencia_inicio, vigencia_fim, objetivos, observacao, acao_orcamentaria_assoc, instrumento_legal, tipo_politica, grande_area, area, created_at, updated_at, publico_alvo_legislacao, link_legislacao) VALUES (358, 'Programa de Garantia da Atividade Agropecuária da Agricultura Familiar (PROAGRO)', '2009-01-01', '462/2009', '14 de maio de 2009', '12058/2009', '2009-10-13', null, 'Assegura ao agricultor familiar, na forma estabelecida pelo Conselho Monetário Nacional: I - a exoneração de obrigações financeiras relativas a operação de crédito rural de custeio ou de parcelas de investimento, cuja liquidação seja dificultada pela ocorrência de fenômenos naturais, pragas e doenças que atinjam rebanhos e plantações; II - a indenização de recursos próprios utilizados pelo produtor em custeio ou em investimento rural, quando ocorrerem perdas em virtude dos eventos citados no inciso I; III - a garantia de renda mínima da produção agropecuária vinculada ao custeio rural.’', ' ', 'NA', 'Lei ordinária', 5, 1, 4, null, null, 'Art. 1º Observado o disposto no artigo 1º da Lei nº 5.969, de 11 de dezembro de 1973, a cobertura do Programa de Garantia da Atividade Agropecuária (PROAGRO), no período agrícola de 1989/90, poderá, também, contemplar exclusivamente os recursos próprios aplicados pelos produtores em seus empreendimentos rurais.(Redação dada pela Lei 7890 de 1989)', null);
